import React, { Component } from 'react';
import styled, {css} from 'styled-components'
import { connect } from 'react-redux';
import axios from 'axios';
import Timer from './Timer';
import TestFinished from './TestFinished';


const Main = styled.div`
	margin: auto;
	max-width: 1200px;
	font-size: 24px;
	overflow: none;
`;
const FlexRow = styled.div`
	display: flex;
	flex-direction: ${props => props.dir || 'row'};
	flex-wrap: wrap;
	margin: 16px 0;
	justify-content: space-between;
	align-items: center;
	width: ${props => props.width || '100%'};
	height: ${props => props.height || '100%'};
	box-sizing: border-box;

	@media screen and (max-width: 1190px) {
		flex-direction: column;
		min-width: 100%;
	}
`;
const FlexChild = styled.div`
	width:  ${props => props.width || '25%'};
	box-sizing: border-box;
		   
	@media screen and (max-width: 1190px) {
		margin: 5px;
		min-width: 100%;
	}
`;
const FlexChildAnswer = styled.div`
	padding: 16px 32px;
	word-wrap: break-word;
	text-align: center;
	background-color: #4F9DA6;
	border-radius: 4px;
	color: white;
	width:  ${props => props.width || '25%'};
	min-height: 60px;
	box-sizing: border-box;
		   
	@media screen and (max-width: 1190px) {
		margin: 5px;
		min-width: 100%;
	}
	:hover {
		background-color: #3A7C84;
	}
`;
const QuestionNumber = styled.div`
	padding-left: 144px;
	font-size: 24px;
	color: #100529;
	height: 44px;
	width: 100%;
	text-align: center;
	line-height: 44px;
	box-sizing: border-box;
`;
const AnswerTitle = styled.div`
	margin-bottom: 16px;
	padding: 32px 16px;
	background-color: rgba(0, 0, 0, 0.5);
	width: 100%;
	min-height: 100px;
	box-sizing: border-box;
	
`;

class TestPassPanel extends Component {
	constructor(props) {
		super(props);
		this.state = {
			testData: {},
			choosed: false,
			testEnd: false,
			totalScore: 0,
			currentQuesion: {},
			testLoaded: false,
		}
    }
   componentDidMount() {	

	axios.get('https://test-project-4ab6b.firebaseio.com/tests/-LS0j3a0GongklJjoPEw.json')
		.then((response) => {
			console.log(response.data.questions)
			this.setState({testData: response.data});
			return this.state.testData
		}).then((testData) => {
			this.setState({testLoaded: true, currentQuesion: testData.questions[0]})
		})	
	}
	currentQuesion = () => {


	}

	testEnded = () => {
		this.setState({testEnd: true});
	}
	render() {
		let questions = [];
		if (this.state.testLoaded) {
			questions = this.state.testData.questions;
		} 
		return (
			<Main>
				{this.state.testLoaded ?
				 <div>
					<FlexRow>
						<FlexChild width={'calc(100% - 144px)'}>
							<QuestionNumber>Question 1/{questions.length}</QuestionNumber>
						</FlexChild>
						<FlexChild width={'144px'}>
							<Timer 
								time={this.state.testData.testDuration}
								testEnded={this.testEnded}
							/> 
						</FlexChild>
					</FlexRow>
					<FlexRow >
						<FlexChild width={'100%'}>
							<AnswerTitle>{questions.length && this.state.currentQuesion.questionTitle}</AnswerTitle>
						</FlexChild>
					</FlexRow>
					{this.state.currentQuesion.answers.map((answer, index) => (
						<FlexRow key={`answer${index}`}>
							<FlexChildAnswer  width={'100%'}>
								{answer.title}
							</FlexChildAnswer>
						</FlexRow>
					))}
					<TestFinished />
				</div>
				: ''
				}
			</Main>
			
		);
	}
}

function mapStateToProps(state) {
	return {
	}
}
function mapDispatchToProps(dispatch) {
	return {
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(TestPassPanel)
