import React, { Component } from 'react';
import styled from 'styled-components';
import QuestionCreater from './QuestionCreater/QuestionCreater';
import {connect} from 'react-redux';
import {deleteQuestions} from '../../store/actions/testCreater';
import {firebase} from '../../firebase/firebase';

const Main = styled.div`
	margin: auto;
	max-width: 1200px;
`;
const TestCreaterLink = styled.div`
	margin: 30px 0;
	font-size: 14px;
`;
const FlexRow = styled.div`
	display: flex;
	flex-direction: row;
	flex-wrap: wrap;
	margin: 30px 0;
	justify-content: space-between;
	align-items: center;
	width: ${props => props.width  || '100%'};
	box-sizing: border-box;

	@media screen and (max-width: 1190px) {
		flex-direction: column;
		min-width: 100%;
	}

`;
const Title = styled.div`
	flex: 50%;
    flex-shrink: 1;
	box-sizing: border-box;
	@media screen and (max-width: 1190px) {
		margin-top: 5px;
		min-width: 100%;
	}
`;
const Type = styled.div `
	flex: 25%;
	text-align: right;
	box-sizing: border-box;
	@media screen and (max-width: 1190px) {
		margin-top: 5px;
		min-width: 100%;
	}
`;
const Company = styled.div `
	flex: 25%;
	box-sizing: border-box;
	@media screen and (max-width: 1190px) {
		margin-top: 5px;
		min-width: 100%;
	}
`;
const TestDetails = styled.input `
	width: ${props => props.width  || 'calc(100% - 16px)'};
	height: 60px;
	padding-left: 16px;
	color: #4F9DA6;
	font-size: 24px;
	overflow: hidden; 
	border: 1px solid #4F9DA6;
	box-sizing: border-box;
`;
const Select = styled.select `
	width: calc(100% - 8px);
	height: 60px;
	padding-left: 16px;
	color: #4F9DA6;
	font-size: 24px;
	overflow: hidden; 
	border: 1px solid #4F9DA6;
	box-sizing: border-box;
`;

const Description = styled.textarea`
	width: 100%;
	height: 120px;
	padding: 16px;
	border: 1px solid #4F9DA6;
	font-size: 24px;
`;
const AddButton = styled.button`
	margin-bottom: 15px;
	width: 234px;
	height: 60px;
	border-radius: 4px;
	border: 0px;
	background-color: #4F9DA6;
	box-shadow: 0px 3px 6px rgba(0, 0, 0, 0.16);
	color: white;
	font-size: 24px;
`;
class TestCreater extends  Component {
	constructor(props) {
		super(props);
		this.state = {
			id:'1', 
			testTitle: '', 
			testDeadline: '', 
			testType: '', 
			company: '', 
			isEdites: false,
		}
	}
	componentDidMount() {
	}
	clearWordFromSpaces = (word) => {
		return  word.replace(/^[ ]+/g, '').replace(/\s*$/, '');
	}
	postData = () => {
		let db = firebase.database();
		this.setState ({
			...this.state,
			passers: 0,
			testTitle: this.clearWordFromSpaces(this.state.testTitle),
			testDeadline: Date(this.state.testDeadline),
			company: this.clearWordFromSpaces(this.state.company),
			question: this.props.questions,
		})

		db.ref('tests').push({...this.state});

		this.setState({
			id: Date.now(),
			testTitle: '', 
			testDeadline: '',
			testType: '', 
			company: '', 
			isEdites: false
		})
	}
	selectBox = () => {
		const languages = ['JavaScript', 'Java', "PHP", 'C#', 'MySQL', 'Python', 'Ruby', 'Swift', 'React', 'Redux'];
		return(
			<Select
				width={'calc(100% - 8px)'}
				required
				onChange={(e) => this.setState({testType: e.target.value})} 
				value={this.state.testType}>
				{
					languages.map((option, index) => (
					<option key={index + 1}> {option} </option>
				))}
			</Select>
		);
	}
	// questionsListCreater = () => {
	// 	return ( 
	// 		<QuestionsDiv width={90}>
	// 		<Ul>
	// 			{this.props.questions.map((question, index) => (
	// 				<Li key={question.id}>
	// 					<Div width={84}>
	// 						{this.props.questions[index].questionTitle}
	// 					</Div>
	// 					<Div width={100} align={'right'}>
	// 						<Button 
	// 							onClick={() => this.editChosenQuestion()}
	// 							width={60} 
	// 							type='button'
	// 						> 
	// 							edit
	// 						</Button>
	// 						<Button 
	// 							onClick={() => this.deleteChosenQuestion(question.id)}
	// 							width={60} 
	// 							type='button'
	// 						> 
	// 							delete
	// 						</Button>
	// 					</Div>
	// 				</Li>))}
	// 		</Ul>
	// 		</QuestionsDiv>
	// 	)
	// }
	submitHandler = (e) => {
		e.preventDefault();
		this.postData();
		this.props.deleteQuestions();
	}	 

	render() {
		return(
		 /* <Div width={100}>
			<FlexContainer width={100} dir={'column'}>
				 <FlexContainer width={100} dir={'column'}>
					<span>Input title of Test</span>
						<TitleField
							required
							rows={2} 
							value={this.state.testTitle}
							onChange={(e) => {this.setState({testTitle: e.target.value})}}
						/>
					  
	          		</FlexContainer>
					  <FlexContainer width={100}>
						<FlexContainer dir='column' >
							Test type  
							{this.selectBox()}
						</FlexContainer>
						<FlexContainer dir='column' >
							Company name
							<Input 
								required
								type='text' 
								placeholder='Input company'
								value={this.state.company}
								onChange={(e) => {this.setState({company: e.target.value})}}
							/>
						</FlexContainer>
						<FlexContainer dir='column' >
							Test deadline
							<Input 
								required
								type='date' 
								placeholder='Input deadline of test'
								value={this.state.testDeadline}
								onChange={(e) => {this.setState({testDeadline: e.target.value})}}
								
							/>
						</FlexContainer>
						</FlexContainer>
	            </FlexContainer>
				</Div>
				<Div width={20}>
					{this.props.questions.length > 0 && <Button width={100}>Add Test</Button>} 	
				</Div>
			</form>
			<Div width={90}>
				
			</Div>
			<Div width={20}></Div>	
			<FlexContainer dir={'column'} width={100}>
				<FlexContainer width={100}>
					{this.props.questions.length > 0 && this.questionsListCreater()}
				</FlexContainer>
			</FlexContainer> */
		<Main>
			<form onSubmit={this.submitHandler}>
				<TestCreaterLink> Home / My Account / Test Create </TestCreaterLink>
				<FlexRow width={'100%'}>
					<Title>
						<TestDetails
							width={'calc(100% - 16px)'} 
							type="text"
							placeholder="Test Title"
							value={this.state.testTitle}
							onChange={(e) => {this.setState({testTitle: e.target.value})}} />
					</Title>
					<Company>
					<TestDetails
							width={'calc(100% - 8px)'} 
							type="text"
							placeholder="Company"
							value={this.state.company}
							onChange={(e) => {this.setState({company: e.target.value})}}/>
					</Company>
					<Type> {this.selectBox()}  </Type>
				</FlexRow>
				<FlexRow width={'100%'}>
					<Description placeholder="Description" />
				</FlexRow>
				<FlexRow width={'100%'}>
					<div>
						<TestDetails
							width={'292px'}
							type='date' 
							placeholder="Deadline"
							onChange={(e) => {this.setState({testDeadline: e.target.value})}} />
					</div>
					<div>
						<TestDetails width={'292px'} type='number' min="0"  placeholder="Minutes" />
					</div>
					<div>
						<TestDetails width={'292px'} type='number' min="0"  placeholder="Total Score" />
					</div>
					<div>
						<TestDetails width={'292px'} type='number' min="0"  placeholder="Passing Score" />
					</div>
				</FlexRow>
				<AddButton>ADD QUESTION</AddButton>
				<QuestionCreater />
			</form>
		</Main>
	);
}

}


function mapStateToProps(state) {
	console.log(state.test.questions);
  return {
	  questions: state.test.questions,
	}
}
function mapDispatchToProps(dispatch) {
  return {
	deleteQuestions: () => dispatch(deleteQuestions()),
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(TestCreater)
