import {CHANGE_TOTAL_SCORE, SUBMITTED_FALSE, SUBMITTED_TRUE, ADD_QUESTION, DELETE_QUESTION, ADD_QUESTION_SUBMITTED, 
  ANSWER_VALID, QUESTION_VALID, ANSWER_NOT_VALID, QUESTION_NOT_VALID, TEST_NOT_VALID, TEST_VALID} from '../actions/actionTypes';

const initialState = {
  questions: [],
  totalScore: 0,
  addQuestionSubmitted: false,
  isAnswerValid: false,
  isQuestionValid: false,
  isTestValid: false,
  submitted: false,
  testValid: false,
  isRight: false,

}

export default function testCreator(state = initialState, action) {
  switch (action.type) {
    case CHANGE_TOTAL_SCORE:
      return {
        ...state,
        totalScore: state.totalScore + action.score,
      }
    case SUBMITTED_FALSE:
      return {
        ...state,
        submitted: false,
      }
    case SUBMITTED_TRUE:
      return {
        ...state,
        submitted: true,
    }
    case ANSWER_VALID:
      return {
        ...state,
        isAnswerValid: true,
    }
    case ANSWER_NOT_VALID:
      return {
        ...state,
        isAnswerValid: false,
      }
    case QUESTION_VALID:
      return {
        ...state,
        isQuestionValid: true,
    }
    case QUESTION_NOT_VALID:
      return {
        ...state,
        isQuestionValid: false,
      }
    case ADD_QUESTION:
      let oneQuestion = {
        id: Date.now(),
        answers: [{ id: Date.now(), title: ''}, { id: Date.now() + 1, title: ''}],
        isRight: 0,
        questionTitle: '',
        score: '',
      }
      return {
        ...state,
        questions: state.questions.concat(oneQuestion),
      }
      case DELETE_QUESTION:
        return {
          ...state,
          questions: state.questions.filter(question => question.id !== action.id),
          totalScore: state.totalScore - action.score,
          isRight: action.id === state.isRight ? 0 : state.isRight
        }
      case ADD_QUESTION_SUBMITTED:
        return {
          ...state,
          addQuestionSubmitted: true,
        }
      case ADD_QUESTION_SUBMITTED:
        return {
          ...state,
          addQuestionSubmitted: true,
      }
      case TEST_NOT_VALID:
        return {
          ...state,
          testValid: false,
      }

      case TEST_VALID:
        return {
          ...state,
          testValid: true,
      }
      default:
        return state
  }
  
}
