import {ADD_QUESTION} from '../actions/actionTypes';

const initialState = {
  questions: [],
  title: '',
  type: '',
  company: '',
  deadline: '',
  edited: false,
}

export default function testReducer(state = initialState, action) {
  switch (action.type) {
    case ADD_QUESTION:
      return {
        ...state,
        questions: state.questions.concat(action.question),
      }
    case TEST_TITLE_HANDLER:
      return {
        ...state,
        title: action.title,
      }
    case COMPANY_HANDLER:
      return {
        ...state,
        company: action.company,
      }
    default:
      return state
  }
}
