import React from 'react';
import {connect} from 'react-redux';
import {setAnswersValues} from '../../../store/actions/questionCreater';
import styled from 'styled-components'

const AnswersField = styled.textarea`
  rows: ${props => props.rows  || '4'};
  width: 100%;
`;

class OneAnswerCreater extends React.Component {
  constructor(props){
     super(props);
     this.state = {title: ''}
  }
  inputChangeHandler = (e) => {
    return new Promise((resolve, reject) => {
      this.setState({title: e.target.value});
      resolve(this.state.title);
    }) 
    .then(title => {
      this.getInputValue();
    }) 
  }
  getInputValue = () => {
    let id = this.props.id;
		let isRight = this.props.isRight;
    let answers = this.props.answers.map(answer => {
      if (answer.id === id) {
        answer.title = this.clearWordFromSpaces(this.state.title);
        answer.isRight = isRight; 

      } 
      return answer; 
    })
    this.props.setAnswersValues(answers);
  }
  clearWordFromSpaces = (word) => {
    return  word && word.replace(/^[ ]+/g, '').replace(/\s*$/, '');
  }
  componentDidUpdate(prevProps) {
    prevProps.isRight !== this.props.isRight && this.getInputValue();
  }
 	render() {
    return ( 
      <AnswersField 
        required
        id={this.props.id} 
        value={this.title} 
        onChange={this.inputChangeHandler}
      />
    )
  }
}

function mapStateToProps(state) {
  return {answers: state.question.answers}
}
function mapDispatchToProps(dispatch) {
  return {
    setAnswersValues: (answers) => dispatch(setAnswersValues(answers))
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(OneAnswerCreater)
