import React, { Component } from 'react';
import styled from 'styled-components';
import QuestionCreater from './QuestionCreater/QuestionCreater';
import {connect} from 'react-redux';
import { stat } from 'fs';

const Ul = styled.ul`
	margin:0;
	padding: 0;
	list-style: none;
	width: 100%;
`;
const Li = styled.li`
`;
const Input = styled.input`
	margin: 5px;
	padding: 10px 5px;
	width: 80%;
	height: 10px;
	font-size: 12px;
`;
const FlexContainer = styled.div`
  display: flex;
  margin: auto;
  padding: 0 10px;
  margin-top: 5px;
  font-size: 14px;
  align-items: center;
  justify-content: space-between;
  width: ${props => props.width + '%' || '100%'};
  flex-direction: ${props => props.dir || 'row'};
  flex-wrap: wrap;
  box-sizing: border-box;
`;
const Button = styled.button`
  margin: 5px;
  font-size: 18px;
  background: transparent;
  border-radius: 5px;
  border: 1px solid ${props => props.color  || ''};
  background-color: ${props => props.color || 'white'};
  width: ${props => props.width + 'px' || '100px'};
  height:${props => props.height + 'px' || '10px'};
`;
const TitleField = styled.textarea`
  rows: ${props => props.rows  || '4'};
  width: 100%;
`;

class TestCreater extends  Component {
	constructor(props) {
		super(props);
	}

	questionsListCreater = () => {
		return ( 
			<Ul>
				{this.props.questions.map(question => (
					<Li key={question.id}>
					<FlexContainer>
						{this.props.questions.questionTitle}
						<Button 
							width={100} 
							type='button'
						> 
							edit
						</Button>
						<Button 
							width={100} 
							type='button'
						> 
							delete
						</Button>
					</FlexContainer>
					</Li>))}
			</Ul>
		)
	} 	  
	render() {
		return(
			<div>
			<form>
			<FlexContainer width={100} dir={'column'}>
				<FlexContainer width={70} dir={'column'}>
	          		<span>Input title of Test</span>
	          		<TitleField
	          			required
	          			rows={2} 
	          			value={''}
	          			id='title'
	          		/> 
	          	</FlexContainer>
	          	<FlexContainer width={70}>
	          		<FlexContainer dir='column' >
	          			Test type  
	          			<Input 
	          				required
	          				type='text' 
	          				placeholder='input type JS, PHP...'
	          		/>
	          	 	</FlexContainer>
	          		<FlexContainer dir='column' >
	          			Company name
	          			<Input 
	          				required
	          				type='text' 
	          				placeholder='Input company'
	          			/>
	          	 	</FlexContainer>
	          	 	<FlexContainer dir='column' >
	          			Test deadline
	          			<Input 
	          				required
	          				type='date' 
	          				placeholder='Input deadline of test'
	          			/>
	          	 	</FlexContainer>
	          	</FlexContainer>
	          	<QuestionCreater />
				{this.questionsListCreater()}
			</FlexContainer>
			</form>
		</div>
		
	);

	}
}

function mapStateToProps(state) {
  return {
	  questions: state.test.questions,
	  title: stat.test.questions.questionTitle,
  }
}
function mapDispatchToProps(dispatch) {
  return {
    
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(TestCreater)
