import React from 'react';
import styled, {css} from 'styled-components'
import {connect} from 'react-redux';

const Radio = styled.input`
	margin: 16px 5px;
	width: 22px;
	height: 22px;

	:before {
		width: 21px;
		height: 21px;
		border-radius: 15px;
		top: -1px;
		left: -1px;
		position: relative;
		background-color: white;
		content: '';
		display: inline-block;
		visibility: visible;
		border: 1px solid rgba(79, 157, 166, 1);
	}
	:checked:after {
		width: 11px;
		height: 11px;
		border-radius: 15px;
		top: -22px;
		left: 4px;
		position: relative;
		background-color: rgba(79, 157, 166, 1);
		content: '';
		display: inline-block;
		visibility: visible;
		border: 1px solid rgba(79, 157, 166, 1);
	}
`;

const DeleteAnswer = styled.button`
		margin: 8px 8px;
		width: 40px;
		height: 40px;
		color:rgba(230, 36, 22, 1);
		font-size: 24px;
		background-color: white;
		border: 1px solid rgba(231, 231, 231, 1);
`;

const AnswerInput = styled.textarea`
  width: calc(100% - 96px);
  height: 60px;
  padding: 16px 16px;
  margin-bottom: 15px;
  color: #4F9DA6;
  font-size: 24px;
  overflow: hidden;
  overflow-wrap: break-word;
  border: 1px solid #4F9DA6;
  box-sizing: border-box; 
  transition: font-size 0.5s ease-in-out;

    @media screen and (max-width: 580px) {
		font-size: 12px;
  }
  ::-webkit-input-placeholder {color: rgba(79, 157, 166, 0.5)}
	::-moz-placeholder          {color:rgba(79, 157, 166, 0.5)}/* Firefox 19+ */
	:-moz-placeholder           {color:rgba(79, 157, 166, 0.5)}/* Firefox 18- */
  :-ms-input-placeholder      {color:rgba(79, 157, 166, 0.5)}

  ${props => props.invalid && css`
    font-size: 22px;
    color: rgba(185, 4, 46, 0.5);
    border-bottom: 1px solid rgba(185, 4, 46, 1);
    ::-webkit-input-placeholder {color: rgba(185, 4, 46, 0.5)}
    ::-moz-placeholder          {color: rgba(185, 4, 46, 0.5)}/* Firefox 19+ */
    :-moz-placeholder           {color: rgba(185, 4, 46, 0.5)}/* Firefox 18- */
    :-ms-input-placeholder      {color: rgba(185, 4, 46, 0.5)}
  `}
`;

class OneAnswerCreator extends React.Component {
  constructor(props){
     super(props);
     this.state = {title: ''}
  }
  titleHandleChange = (e) => {
      let value = this.props.clearWordFromSpaces(e.target.value);
      this.props.getAnswersValues(this.props.id, value);
      
    }
  isRightHandleChange = () => {
    this.props.getInputValues('isRight');
  }
   

  componentDidUpdate(prevProps, prevState) {
    //  if (prevProps.submitted !== this.props.submitted 
    //     || (this.props.submitted && (prevState.title !== this.state.title))) {
    //   if (this.props.submitted) {
    //     this.isAnswerValid();
    //     this.props.getInputValue(this.clearWordFromSpaces(this.state.title), this.props.id, this.props.isRight);
     //   }
      
    //}
  }

 	render() {
    return ( 
      <>
        <Radio
          type='radio'
          name={`question${this.props.id}`} 
          value={this.props.id} 
          onClick={(e) => this.setState({isRight: +e.target.value})}
        />

        <AnswerInput 
          type="text"
          placeholder={`Answer title ${this.props.count}`} 
          onChange={this.handleChange} 
          invalid = {this.props.isFilled(this.state.title)}
          id={this.props.id} 
        />

        <DeleteAnswer 
          type='button'
          id='delete'
          onClick={(e) => this.answersCountHandler(e, input.id)}
        >
          X 
        </DeleteAnswer> 
      </>
    );
   }
  }

function mapStateToProps(state) {
  return {
	 submitted: state.testCreator.submitted,
  }
}

export default connect(mapStateToProps, null)(OneAnswerCreator)
