import {ADD_ANSWER, SET_ANSWERS_VALUES, DECREASE_ANSWERS_COUNT, 
  DELETE_CHOSEN_ANSWER, RIGHT_ANSWER_CHOSEN} from './actionTypes'

export function addAnswer(answer) {
  console.log('this is action');
  return {
    type: ADD_ANSWER,
    answer
  }
}
export function setAnswersValues(answers) {
  console.log('this is action');
  return {
    type: SET_ANSWERS_VALUES,
    answers
  }
}
export function decreaseAnswersCount() {
  console.log('this is action');
  return {
    type: DECREASE_ANSWERS_COUNT,
  }
}
export function deleteChosenAnswer(id) {
  console.log('this is action');
  return {
    type: DELETE_CHOSEN_ANSWER,
    id
  }
}
export function rightAnswerChosen(isRight) {
  return {
    type: RIGHT_ANSWER_CHOSEN,
    isRight
  }
}
