import React from 'react';
import OneAnswerCreater from './OneAnswerCreater';
import styled from 'styled-components'
import {connect} from 'react-redux';
import {addAnswer, decreaseAnswersCount, deleteChosenAnswer} from '../../../store/actions/createAnswer';

export const RadioButton = () => {
	const RadioButton = styled.input`
		width:30px;
		height:30px;
		color: green;
	`;
	return <RadioButton type='radio' name='rightAnswer'/>
}
const Ul = styled.ul`
	list-style: none;
	width: 100%;
`;
const Li = styled.li`
	margin: 5px;
	verticla-align: middle;
`;
const FlexContainer = styled.div`
	display: flex;
  margin-top: 10px;
  align-items: center;
  justify-content: space-between;
  width: ${props => props.width + 'em' || '10'};
  flex-direction: ${props => props.dir || 'row'};
`;
const Button = styled.button`
  margin: 5px;
  padding: 2px;
  font-size: 18px;
  background: transparent;
  border-radius: 3px;
  border: 1px solid ${props => props.color  || ''};
  background-color: ${props => props.color || 'white'};
  width: ${props => props.width + 'px' || ''};
  height:${props => props.height+ 'px' || ''};
`;
const TitleField = styled.textarea`
  rows: ${props => props.rows  || '4'};
  width: 100%;
`;
class QuestionCreater extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          isRight: 0,
        };
    }
    componentDidMount() {
      this.answersListCreater();
    }
    rightAnswerChangeHandler = (e) => {
      this.setState({isRight: +e.target.value});
    }
    addAnswer = () => {
    	let newAnswer = {id: Date.now(), title: '', isRight: false}
      this.props.addAnswer(newAnswer);
    }
    answersListCreater = () => {
      return ( 
        <Ul>
          {this.props.answers.map(input => (
            <Li key={input.id}>
              <FlexContainer width={30}>
                <RadioButton 
                  value={input.id} 
                  onChange={this.rightAnswerChangeHandler}
                />
                <OneAnswerCreater 
                  id={input.id} 
                  isRight={input.id === this.state.isRight ? true : false}
                />
                <Button width={50} onClick={() => this.deleteChosenAnswer(input.id)}> X </Button>
              </FlexContainer>
          	</Li>))}
        </Ul>
      )
    }   
    inputsCountChangeHandler = (e) => {
      e.target.id === 'plus' && this.props.count <= 4 && this.addAnswer();
   		e.target.id === 'minus' && this.props.count >= 3 && this.props.decreaseAnswersCount();
      this.answersListCreater();
    }
    deleteChosenAnswer = (id) => {
    	this.props.count > 2 && this.props.deleteChosenAnswer(id);
    }
    render() {
      return (
        <div>
          <FlexContainer width={30} dir={'column'}>
          	<FlexContainer width={20} dir={row}>
          		<span>Input Question</span>
          		<TitleField rows={3} /> 
          	</FlexContainer>
            <FlexContainer width={20}>
              <span>Count of answers</span> 
                <Button width={20} height={20} onClick={this. inputsCountChangeHandler} id='minus'> - </Button>
                  {this.props.count}
                <Button width={20} height={20} onClick={this. inputsCountChangeHandler} id='plus'> + </Button>
            </FlexContainer>
          	{this.answersListCreater()}
          </FlexContainer>
        </div>
      );
    }
}
function mapStateToProps(state) {
  return {
  	answers: state.question.answers,
  	count: state.question.answersInputCount,
  }
}
function mapDispatchToProps(dispatch) {
  return {
    addAnswer: (answers) => dispatch(addAnswer(answers)),
    decreaseAnswersCount: () => dispatch(decreaseAnswersCount()),
    deleteChosenAnswer: (id) => dispatch(deleteChosenAnswer(id)),
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(QuestionCreater)
