import React, { Component } from 'react';
import styled, {css} from 'styled-components'
import js_svg from './img/js.svg';
//import cplus_svg from './img/Cplus.svg';
import php_svg from './img/php.svg';
import react_svg from './img/react.svg';
//import css_svg from './img/C++.svg';
//import sharp_svg from './img/csharp.svg';
//import html_svg from './img/html.svg';

const FlexChild = styled.div`
	position: relative;
	box-sizing: border-box;
	width: ${props => props.width || '350px'}; 
	heigth: ${props => props.width || '450px'};
	     
	@media screen and (max-width: 1190px) {
		margin: 10px 5px;
		min-width: 98%;
	}
`;

const FlexRow = styled.div`
	display: flex;
	flex-direction: ${props => props.dir || 'row'};
	flex-wrap: wrap;
	padding: 30px 16px;
	width: ${props => props.width || '350px'}; 
	heigth: ${props => props.width || '450px'};
	box-sizing: border-box;

	@media screen and (max-width: 1190px) {
		flex-direction: column;
		min-width: 100%;
	}
`;
const TestTitle = styled.div`
	font-size: 20px;
	color: #4F9DA6;
	font-weight: bold;
	margin-bottom: 8px;
`;
const Company = styled.div`
	font-size: 16px;
	color: #FFAD5A;
	font-weight: bold;
	margin-bottom: 8px;
`;

const Data = styled.span`
	font-size: 16px;
	color: #4F9DA6;
	margin-bottom: 8px;

`;

const ImgDiv = styled.div`
	width: 350px;
	height: 240px;
	margin-bottom: 16px;
`;

const DataTitle = styled.div`
	font-size: 16px;
	color: black;
	margin-bottom: 8px;
`
export default class TestComponent extends Component {
	constructor(props){
		super(props);
		this.state = {
			id: this.props.id,
			img: '',
			title: this.props.title,
			company: this.props.company,
			passes: this.props.passes,
			deadline: this.props.deadline,
        }
	}
	componentDidMount() {
		this.chooseImg(this.props.type)
	}
	chooseImg = (type) => {
		let img = '';
		switch (this.props.type) {
			case 'JavaScript' :
				return img = {js_svg};
			case 'PHP' :
				return img = {php_svg};
			case 'C#' :
				return img = {sharp_svg};
			case 'React' :
				return img = {react_svg};
		
		}

		this.setState({img: img});
	}
    render() {
        return(
			<FlexRow>
				<ImgDiv>
					{/* {!this.state.img && this.stata.img} */}
					<img src={js_svg} alt='js' />
					<TestTitle>
						{/* {this.state.title} */} js Foundemantals
					</TestTitle>
				</ImgDiv>
				<Company>
					{/* {this.state.company} */} Company
				</Company>
				<DataTitle>
					Passes: 
					<Data>
						{/* {this.props.passes} */} 0
					</Data>
				</DataTitle>
				<DataTitle>
					Deadline:
					<Data>
						{/* {this.props.deadline} */}10/12/18
					</Data>
				</DataTitle>
				
			</FlexRow>
        );
    
	}
}

