import React, { Component } from 'react';
import styled, {css} from 'styled-components'
import { connect } from 'react-redux';
import img from './img/rawpixel-558596-unsplash.jpg'

const Main = styled.div`
	margin: auto;
	max-width: 1920px;
	font-size: 24px;
    overflow: auto;
  
`; 
const Header = styled.div`
    position: relative;
    width: 100%;   
    height: 700px;		
    background-image: url(${img});
    background-position: center; 
    background-repeat: no-repeat; 
    background-size: cover;
    box-sizing: border-box;
`;

const GetStartedDiv = styled.div`
    position: absolute;
    bottom:0;
    color: white;
    width: 100%;   
    min-height: 100px;	
    z-index: 1;
    background-color: rgba(20, 18, 24, 0.75);
    font-size: 24px;	
`;

const FlexRow = styled.div`
    display: flex;
    margin: 18px auto;
    max-width: 1200px;
    flex-direction: row;
	flex-wrap: wrap;
    box-sizing: border-box;

	@media screen and (max-width: 1190px) {
		flex-direction: column;
		min-width: 100%;
	}
`;

const FlexChild = styled.div`
	position: relative;
	box-sizing: border-box;
	width: ${props => props.width || ''};   
	@media screen and (max-width: 1190px) {
		margin: 20px 5px;
		min-width: 98%;
	}
`;
const GetsStartedText = styled.span`
    font-size: 24px;
`;
const GetStartedButton = styled.button`
    height: 60px;
    border: 0;
    border-radius: 4px;
    padding: 16px;
    background-color:rgba(255, 89, 89, 1);
    color: white;
    font-weight: bold;
    font-size: 18px;
    box-sizing: border-box;
`;
const Title = styled.span`
    font-size: 34px;
    color:rgba(79, 157, 166, 1);
`;

class HomePage extends Component {
	constructor(props) {
		super(props);
		this.state ={};
	}

	render() {
		return (
			<Main>
                <Header>
                    <GetStartedDiv>
                        <FlexRow>
                            <FlexChild width={'1018px'}>
                                <GetsStartedText>
                                    DigiLearn is an all-in-one platform 
                                    that makes it easy to create online tests, launch programming 
                                    companies, build landing pages, 
                                    and design the perfect website.
                                </GetsStartedText>
                            </FlexChild>
                            <FlexChild>
                                <GetStartedButton>
                                    GET STARTED
                                </GetStartedButton>
                            </FlexChild>
                        </FlexRow>
                    </GetStartedDiv>
                    <FlexRow>
                        <Title>
                             YOUR FIRST DESTINATION 
                        </Title>
                    </FlexRow>
                        <FlexRow>
                             <FlexChild>

                            </FlexChild>
                            <FlexChild>
                                
                            </FlexChild>
                    </FlexRow>
                </Header>
            </Main>
		);
	}
}

function mapStateToProps(state) {
	return {
	
	}
}

export default connect(mapStateToProps, null)(HomePage)
