import {CHANGE_TOTAL_SCORE, SUBMITTED_FALSE, SUBMITTED_TRUE, ADD_QUESTION, DELETE_QUESTION, ADD_QUESTION_SUBMITTED, IS_ANSWER_VALID, IS_QUESTION_VALID } from '../actions/actionTypes';

const initialState = {
  questions: [],
  totalScore: 0,
  addQuestionSubmitted: false,
  isAnswerValid: false,
  isQuestionValid: false,
  isTestValid: false,

}

export default function testCreator(state = initialState, action) {
  switch (action.type) {
    case CHANGE_TOTAL_SCORE:
      return {
        ...state,
        totalScore: state.totalScore + action.score,
      }
    case SUBMITTED_FALSE:
      return {
        ...state,
        submitted: false,
      }
    case SUBMITTED_TRUE:
      return {
        ...state,
        submitted: true,
    }
    case IS_ANSWER_VALID:
      return {
        ...state,
        isAnswerValid: true,
    }
    case IS_QUESTION_VALID:
      return {
        ...state,
        isQuestionValid: true,
    }
    case ADD_QUESTION:
      let oneQuestion = {
        id: Date.now(),
        answers: [{ id: Date.now(), title: ''}, { id: Date.now() + 1, title: ''}],
        isRight: 0,
        questionTitle: '',
        score: '',
      }
      return {
        ...state,
        questions: state.questions.concat(oneQuestion),
      }
      case DELETE_QUESTION:
        return {
          ...state,
          questions: state.questions.filter(question => question.id !== action.id),
          totalScore: state.totalScore - action.score,
        }
      case ADD_QUESTION_SUBMITTED:
        return {
          ...state,
          addQuestionSubmitted: true,
        }
      default:
        return state
  }
  
}
