import React from 'react';
import {NavLink} from "react-router-dom";
import styled from 'styled-components';
import * as firebase from "firebase";


const HeaderWrapper = styled.div`
       margin: 0 auto;
       width: 1200px;
       height: 100px;
       display: flex;
       justify-content: space-between;
       align-items: center;
`;

const Logo = styled.div`
    width: 195px;
    height: 60px;
    display: flex;
    cursor: pointer;
    justify-content: center;
    align-items: center;
    color: white;
    font-size: 20px;
    background-color: #FF5959;
`;

const HeaderNavigation = styled.div`
    display: flex;
    width: 430px;
    justify-content: space-between;
`;

const StyledLink = styled(NavLink)`
    color: white;
    font-size: 20px;
    font-weight: bold;
    text-decoration: none;
    cursor: pointer;

    &:link{
        text-decoration: none;
     }

    &:active, &:hover, &:focus {
        color: #FFAD5A;
        text-decoration: underline;
    }
`;

const LoginLogout = styled.div`
    padding: 20px, 30px;
    font-size: 20px;
    border: 1px solid #FFFFFF;  
    text-decoration: underline;
    cursor: pointer;
`;


const Header = (props) => {

    const logOut = () => {

        firebase.auth().signOut().then(function () {
        }, function (error) {
            console.error('Sign Out Error', error);
        });
    };

    return (

        <header style={{
            backgroundColor: '#141218',
            width: '100%',
        }}>

            <HeaderWrapper>
                <NavLink to={'/'}>
                    <Logo>LOGO</Logo>
                </NavLink>

                <HeaderNavigation>
                    <StyledLink to={'/tests'}>TESTS</StyledLink>
                    <StyledLink to={'/companies'}>COMPANIES</StyledLink>
                    <StyledLink to={'/users'}>USERS</StyledLink>
                    <StyledLink to={'/AboutUs'}>ABOUT US</StyledLink>
                </HeaderNavigation>

                {props.currentLog ?
                    <div style={{
                        display: 'flex',
                        alignItems: 'center'
                    }}>
                        <NavLink
                            style={{color: 'white', marginRight: '10px', border: '1px solid black', padding: '10px'}}
                            to={'/company/profile'}>MY ACCOUNT</NavLink>
                        <div className='header-log-in' onClick={() => logOut()}>LOG OUT</div>
                    </div>

                    : <StyledLink to={'/authorization'}>
                        <LoginLogout>LOG IN</LoginLogout >
                    </StyledLink>}
            </HeaderWrapper>

        </header>
    )
};

export default Header;
