import React, {Component} from 'react';
import Login from "./login/Login";
import CompanyRegistration from "./registration/CompanyRegistration";
import * as firebase from "firebase";
import styled from 'styled-components';

const TabDiv = styled.div`
    border-bottom: 1px solid rgba(79, 157, 166, 1);
`;
const Tab = styled.div`
    font-size: 24px;
    padding: 16px 70px;
    background-color: rgba(79, 157, 166, 1);
    color: white;
    font-weight: bold;
    
`;


class AutorizationCompany extends Component {

    constructor(props) {
        super(props);

        this.changeHandler = this.changeHandler.bind(this);

        this.state = {
            pass: '',
            email: '',

            showError: false,
        }
    }

    changeHandler(e, field) {
        this.setState({
            [field]: e.target.value,
        })
    }


    signIn(e) {
        e.preventDefault();
        firebase.auth().signInWithEmailAndPassword(this.state.email, this.state.pass)
            .then(r => {
                localStorage.setItem("current", "company");
                this.props.userLogin('company');
                console.log(r.user)
            })
            .catch(err => console.log(err));
    }

    render() {

        return (
            <div>
                    <TabDiv>
                        <Tab>COMPANY</Tab>
                    </TabDiv>
                    <div className='container'>
                        <Login
                            login={this.state.pass}
                            email={this.state.email}
                            changeHandler={this.changeHandler}
                            signIn={this.signIn.bind(this)}/>
                        <CompanyRegistration/>
                    </div>

            </div>
        );
    }
}



export default AutorizationCompany;
