import React, { Component } from 'react';
import styled, {css} from 'styled-components'
import { connect } from 'react-redux';
import TestFinished from './TestFinished';


const Main = styled.div`
	margin: auto;
	max-width: 1200px;
	font-size: 24px;
	color: white;
	overflow: none;
`;

class TestPassPanel extends Component {
	constructor(props) {
		super(props);
		this.state = {
			testData: {},
			testEnd: false,
			totalScore: 0,
		}
    }
   componentDidMount() {	

	axios.get('https://test-project-4ab6b.firebaseio.com/tests/-LS0j3a0GongklJjoPEw.json')
		.then((response) => {
			console.log(response.data.questions)
			this.setState({testData: response.data});
			return this.state.testData
		}).then((testData) => {
			this.setState({testLoaded: true, currentQuesion: testData.questions[0]})
		})	
	}
	currentQuesion = () => {


	}

	testEnded = () => {
		this.setState({testEnd: true});
	}
	render() {
		
		return (
			<Main>
				{
					!this.state.testEnd 
						? <TestPassPanel />
						: <TestFinished />
				}
			</Main>
			
		);
	}
}

