import {ADD_ANSWER, SET_ANSWERS_VALUES, DECREASE_ANSWERS_COUNT, DELETE_CHOSEN_ANSWER, 
  RIGHT_ANSWER_CHOSEN, QUESTION_TITLE_HANDLER, TIME_HANDLER, SCORE_HANDLER} from '../actions/actionTypes'

const initialState = {
  answers: [{id: Date.now(), title: '', isRight: false}, {id: Date.now()+1, title: '', isRight: false}],
  isRight: 0,
  questionTitle: '',
  time: '',
  score: '',
}

export default function questionReducer(state = initialState, action) {
  switch (action.type) {
    case ADD_ANSWER:
      return {
        ...state,
        answers: state.answers.concat(action.answer),
      }
      case SET_ANSWERS_VALUES:
      return {
        ...state,
        answers : action.answers
      }
      case DECREASE_ANSWERS_COUNT:
      return {
        ...state,
        answers : state.answers.splice(1),
      }
      case DELETE_CHOSEN_ANSWER:
      return {
        ...state,
        answers : state.answers.filter(answer => answer.id !== action.id),
      }
      case RIGHT_ANSWER_CHOSEN:
      return {
        ...state,
        isRight: action.isRight,
      }
      case QUESTION_TITLE_HANDLER:
      return {
        ...state,
        questionTitle: action.title,
      }
      case SCORE_HANDLER:
        return {
          ...state,
         score: action.score,
      }
      case TIME_HANDLER:
        return {
          ...state,
         time: action.time,
      }

      default:
      return state
  }
}
