import React, {Component} from 'react';
import {NavLink} from "react-router-dom";
import styled, {css} from 'styled-components';
import * as firebase from "firebase";


const HeaderWrapper = styled.div`
       margin: 0 auto;
       width: 1200px;
       height: 100px;
       display: flex;
       justify-content: space-between;
       align-items: center;
`;

const Logo = styled.div`
    width: 195px;
    height: 60px;
    display: flex;
    cursor: pointer;
    justify-content: center;
    align-items: center;
    color: white;
    font-size: 20px;
    background-color: #FF5959;
`;

const HeaderNavigation = styled.div`
    display: flex;
    width: 430px;
    justify-content: space-between;
`;

const StyledLink = styled(NavLink)`
    color: white;
    font-size: 20px;
    font-weight: bold;
    text-decoration: none;
    cursor: pointer;

    &:link{
        text-decoration: none;
     }

    &:active, &:hover, &:focus {
        color: #FFAD5A;
        text-decoration: underline;
        
    }
`;

const LoginLogout = styled(NavLink)`
    color: white;
    font-size: 20px;
    font-weight: bold;
    padding: 20px 40px;
    border: 1px solid #FFFFFF;  
    text-decoration: underline;
    cursor: pointer;

    :active, :hover, :focus {
        border-color: #FFAD5A;
        color: #FFAD5A;
       
    }
 
`;
const MyAccount =  styled(NavLink)`
    color: white; 
    marginRight: 10px; 
    border: 1px solid black;  
    padding: 10px;
    text-decoration: underline;
    cursor: pointer;

    :active, :hover, :focus, :visited {
        color: #FFAD5A;
    }
`;


class Header extends Component {
    constructor(props) {
        super(props);
        this. state = {currentLog: null}
    }

    logOut = () => {
        //console.log(props.currentLog)
        firebase.auth().signOut().then(function () {
        }, function (error) {
            console.error('Sign Out Error', error);
        });
    };

    componentDidUpdate = (prevProps) => {
        if (prevProps.currentLog !== this.props.currentLog) {
            this.setState({currentLog: this.props.currentLog});
            console.log(this.state.currentLog.name)
        }
    }

    render() {

    return (
        
        <header style={{
            backgroundColor: '#141218',
            width: '100%',
        }}>

            <HeaderWrapper>
                <NavLink to={'/'}>
                    <Logo>LOGO</Logo>
                </NavLink>

                <HeaderNavigation>
                    <StyledLink to={'/tests'}>TESTS</StyledLink>
                    <StyledLink to={'/companies'}>COMPANIES</StyledLink>
                    <StyledLink to={'/users'}>USERS</StyledLink>
                    <StyledLink to={'/AboutUs'}>ABOUT US</StyledLink>
                </HeaderNavigation>

                {this.state.currentLog?
                    <div style={{
                        display: 'flex',
                        alignItems: 'center'
                    }}>
                        <MyAccount to={'/company/profile'}>{this.state.currentLog.name}</MyAccount>
                        <LoginLogout to={'/company/profile'} onClick={() => this.logOut()}>LOG OUT</LoginLogout>
                    </div>

                    : <LoginLogout to={'/authorization'}>
                        LOG IN
                    </LoginLogout>}
            </HeaderWrapper>

        </header>
    )
            
}
};

export default Header;
