import React, { Component } from 'react';
import styled, {css} from 'styled-components'
import { connect } from 'react-redux';
import mainBackground from './img/main.jpg';
import forUserImg from './img/forUser.jpg';
import forCompanyImg from './img/forCompany.jpg';
import checkSvg from './img/check-solid.svg'

const Main = styled.div`
	margin: auto;
	max-width: 1920px;
	font-size: 24px;
    overflow: auto;
  
`; 
const Header = styled.div`
    position: relative;
    width: 100%;   
    height: 700px;		
    background-image: url(${mainBackground});
    background-position: center; 
    background-repeat: no-repeat; 
    background-size: cover;
    box-sizing: border-box;
`;

const GetStartedDiv = styled.div`
    position: absolute;
    bottom:0;
    color: white;
    width: 100%;   
    height: ${props => props.height || '100px'};  
    z-index: 1;
    background-color: rgba(20, 18, 24, 0.75);
    font-size: 24px;	
`;

const FlexRow = styled.div`
    display: flex;
    margin: 19px auto;
    max-width: 1200px;
    flex-direction: row;
    justify-content: ${props => props.content || 'space-between'};  ;
	flex-wrap: wrap;
    box-sizing: border-box;

	@media screen and (max-width: 1190px) {
		flex-direction: column;
		min-width: 100%;
	}
`;

const FlexChild = styled.div`
	position: relative;
	width: ${props => props.width || ''};  
    box-sizing: border-box; 
    
	@media screen and (max-width: 1190px) {
		margin: 20px 5px;
		min-width: 98%;
	}
`;
const GetsStartedText = styled.span`
    font-size: 24px;
`;
const Button = styled.button`
    margin: 0 16px;
    width: ${props => props.width || ''};  
    height: 60px;
    border: 0;
    border-radius: 4px;
    padding: 16px;
    background-color:rgba(255, 89, 89, 1);
    color: white;
    font-weight: bold;
    font-size: 18px;
    box-sizing: border-box;
`;
const Title = styled.div`
    width: 100%;
    margin-top: 76px;
    margin-bottom: 30px;
    font-size: ${props => props.size || '34px'};  
    color:rgba(79, 157, 166, 1);
    border-bottom: 1px solid rgba(220, 220, 220, 1);
`;
const TitleWhite = styled.div`
    padding: 100px 16px 16px 16px;
    color: white;
    font-size: 48px;
    font-weight: bold;
    box-sizing: border-box;
   
`;
const BlackShadow = styled.div`
    position: absolute;
    width: 588px;
    height: 500px;
    background-color: rgba(20, 18, 24, 0.5);
`;
const ForCompany = styled.div`
    position: relative;
    width: 588px;
    height: 500px;
    background-image: url(${forCompanyImg});
    background-position: center; 
    background-repeat: no-repeat; 
    background-size: cover;
    box-sizing: border-box;
`;
const CheckBoxDiv = styled.div`
    position: relative;
    margin: 16px 16px;
`;
const CheckBox = styled.input`
    display: inline-block;
    margin-right: 16px;
    vertical-align: middle;
    width: 24px; 
    height: 24px;
    
    :before {
        content: '';
        display: inline-block;
        background: #FFAD5A;
        padding: 4px;
        width: 24px; 
        height: 24px;
        text-align: center;
        color: white;
    }
    :checked:after {
        position: absolute;
        display: block;
        top:0;
        padding: 7px 4px;
        content: '';
        background-image:url(${checkSvg});
        background-position: center; 
        background-repeat: no-repeat; 
        background-size: 24px;
        height: 24px;
        width: 24px;
    }
     
`;
const CheckBoxLabel = styled.label`
    display: inline;
    line-height: 24px
    color: white;
    font-size: 24px;
`;

class HomePage extends Component {
	constructor(props) {
		super(props);
		this.state ={};
	}

	render() {
		return (
			<Main>
                <Header>
                    <GetStartedDiv>
                        <FlexRow>
                            <FlexChild width={'1018px'}>
                                <GetsStartedText>
                                    DigiLearn is an all-in-one platform 
                                    that makes it easy to create online tests, launch programming 
                                    companies, build landing pages, 
                                    and design the perfect website.
                                </GetsStartedText>
                            </FlexChild>
                            <FlexChild>
                                <Button widht={'155px'}>
                                    GET STARTED
                                </Button>
                            </FlexChild>
                        </FlexRow>
                    </GetStartedDiv>
                </Header>
                <FlexRow>
                        <Title>
                            YOUR FIRST DESTINATION 
                        </Title>
                        
                        <FlexChild>
                            <ForCompany>
                                <BlackShadow>
                                    <TitleWhite>
                                        FOR COMPANY  
                                    </TitleWhite>
                                    <CheckBoxDiv>
                                        <CheckBox type='checkbox' />
                                        <CheckBoxLabel>
                                            Register Your Company
                                        </CheckBoxLabel>
                                    </CheckBoxDiv> 
                                    
                                    <GetStartedDiv>
                                    <FlexRow content={'flex-end'}>
                                        <Button width={'228px'}>
                                            BECOME COMPANY
                                        </Button>
                                    </FlexRow>
                                    </GetStartedDiv>
                                </BlackShadow>
                            </ForCompany>
                        </FlexChild>
                    </FlexRow>
                        <FlexRow>
                            
                            <FlexChild>
                                
                            </FlexChild>
                    </FlexRow>
            </Main>
		);
	}
}

function mapStateToProps(state) {
	return {
	
	}
}

export default connect(mapStateToProps, null)(HomePage)
