import styled  from 'styled-components';
import React from 'react';
import OneAnswerCreater from '../../containers/QuizCreator/QuestionCreater/OneAnswerCreater';

const DeleteAnswer = styled.button`
	margin-left: 8px;
	width: 40px;
	height: 40px;
	color:rgba(230, 36, 22, 1);
	font-size: 24px;
	background-color: white;
	border: 1px solid rgba(231, 231, 231, 1);	
`;
const Radio = styled.input`
	margin: 5px;
	width: 22px;
	height: 22px;

	:before {
		width: 21px;
		height: 21px;
		border-radius: 15px;
		top: -1px;
		left: -1px;
		position: relative;
		background-color: white;
		content: '';
		display: inline-block;
		visibility: visible;
		border: 1px solid rgba(79, 157, 166, 1);
	}
	:checked:after {
		width: 11px;
		height: 11px;
		border-radius: 15px;
		top: -22px;
		left: 4px;
		position: relative;
		background-color: rgba(79, 157, 166, 1);
		content: '';
		display: inline-block;
		visibility: visible;
		border: 1px solid rgba(79, 157, 166, 1);
	}
`;

export default function QuestionListCreater (props) {
 
  	return ( 
			props.answers.map((input, index) => (
				<div key={input.id}>
					<Radio
						type='radio'
						name={`question${props.id}`} 
						value={input.id} 
						onClick={(e) => props.isRightHandler(+e.target.value)}
					/>
					<OneAnswerCreater
						valid={props.answerValid}
						count={index + 1}
						id = {input.id}
						isRight={input.id === props.isRight ? true : false}
						getInputValue={props.getInputValue}
					/>
					{props.answers.length > 2 && 
					<DeleteAnswer 
						type='button'
						id='delete'
						onClick={(e) => props.answersCountHandler(e, input.id)}
					>
						X 
					</DeleteAnswer>}
			</div>))
		);
}
