import styled from 'styled-components';
import React from 'react';

const DescriptionDiv = styled.div`
		position: absolute;
        padding: 5px 10px;
        width: 100%;
        height: 80%;
		left: 0px;
		top: 0px;
		color: rgba(185, 4, 46, 0.7);
		font-size: 14px;
		background-color: rgba(79, 157, 166, 0.2);
		border-radius: 4px;
		transition: top 0.5s ;

		@media screen and (max-width: 1188.5px) {
			font-size: 12px;
			left: 10px;
			top: 380px;
		}		
`;
