// 		@media screen and (max-width: 1190px) {
// 			left: 0px;
// 			top: 23px;
// 		}	
// `;

const TestDescription = props => {
    return (
		<DescriptionDiv>
				{/* <Trinagle ></Trinagle>  */}
				  {props.description}
		</DescriptionDiv >
	)
}

export default TestDescription;
