import React, { Component } from 'react';
import styled from 'styled-components';
import QuestionCreater from './QuestionCreater/QuestionCreater';
import {connect} from 'react-redux';
import {deleteQuestions} from '../../store/actions/testCreater';
import {firebase} from '../../firebase/firebase';

const TestCreaterLink = styled.div`
	margin: 30px 0;
	font-size: 14px;
`;
const FlexRow = styled.div`
	display: flex;
	flex-direction: row;
	flex-wrap: wrap;
	margin: 30px 0;
	justify-content: space-between;
	align-items: center;
	width: ${props => props.width  || '100%'};
	box-sizing: border-box;
`;
const Title = styled.div`
	flex: 50%;
    flex-shrink: 1;
	box-sizing: border-box;
`;
const TestDetails = styled.input `
	width: ${props => props.width  || 'calc(100% - 16px)'};
	height: 60px;
	padding-left: 16px;
	color: #4F9DA6;
	font-size: 24px;
	overflow: hidden; 
	border: 1px solid #4F9DA6;
	box-sizing: border-box;
`;
const Date = styled.div `
	flex: 25%;
	box-sizing: border-box;
`;
const Description = styled.textarea`
	width: 100%;
	height: 120px;
	padding: 16px;
	border: 1px solid #4F9DA6;
	font-size: 24px;
`;
const Minutes = styled.div`
	flex: calc(32.57%  + 16px);
	box-sizing: border-box;
`;
const Score = styled.div`
	flex: calc((97.71% / 3));
	box-sizing: border-box;
`;
const PassScore = styled.div`
	flex: calc((97.71% / 3));
	text-align: right;
	box-sizing: border-box;
`;
const AddButton = styled.button`
	margin: 15px 0;
	width: 234px;
	height: 60px;
	border-radius: 4px;
	border: 0px;
	background-color: #4F9DA6;
	box-shadow: 0px 3px 6px rgba(0, 0, 0, 0.16);
	color: white;
	font-size: 24px;
`;
class TestCreater extends  Component {
	constructor(props) {
		super(props);
		this.state = {
			id: Date.now(), 
			testTitle: '', 
			testDeadline: '', 
			testType: '', 
			company: '', 
			isEdites: false,
		}
	}
	componentDidMount() {
		fetch('https://testing-f6114.firebaseio.com/.json')
		.then(responce => console.log(responce.json()));
		
	}
	clearWordFromSpaces = (word) => {
		return  word.replace(/^[ ]+/g, '').replace(/\s*$/, '');
	}
	postData = () => {
		let db = firebase.database();
		this.setState ({
			...this.state,
			passers: 0,
			testTitle: this.clearWordFromSpaces(this.state.testTitle),
			testDeadline: Date(this.state.testDeadline),
			company: this.clearWordFromSpaces(this.state.company),
			question: this.props.questions,
		})

		db.ref('tests').push({...this.state});

		this.setState({
			id: Date.now(),
			testTitle: '', 
			testDeadline: '',
			testType: '', 
			company: '', 
			isEdites: false
		})
	}
	selectBox = () => {
		const languages = ['JavaScript', 'Java', "PHP", 'C#', 'MySQL', 'Python', 'Ruby', 'Swift', 'React', 'Redux'];
		return(
			<TestDetails
				width={'calc(100% - 8px)'}
				required
				onChange={(e) => this.setState({testType: e.target.value})} 
				value={this.state.testType}>
				{
					languages.map((option, index) => (
					<option key={index + 1}> {option} </option>
				))}
			</TestDetails>
		);
	}
	questionsListCreater = () => {
		return ( 
			<QuestionsDiv width={90}>
			<Ul>
				{this.props.questions.map((question, index) => (
					<Li key={question.id}>
						<Div width={84}>
							{this.props.questions[index].questionTitle}
						</Div>
						<Div width={100} align={'right'}>
							<Button 
								onClick={() => this.editChosenQuestion()}
								width={60} 
								type='button'
							> 
								edit
							</Button>
							<Button 
								onClick={() => this.deleteChosenQuestion(question.id)}
								width={60} 
								type='button'
							> 
								delete
							</Button>
						</Div>
					</Li>))}
			</Ul>
			</QuestionsDiv>
		)
	}
	editChosenQuestion = () => {
		return <Modal> <QuestionCreater /></Modal>
	} 
	submitHandler = (e) => {
		e.preventDefault();
		this.postData();
		this.props.deleteQuestions();
	}	 

	render() {
		return(
		 /* <Div width={100}>
			<FlexContainer width={100} dir={'column'}>
				 <FlexContainer width={100} dir={'column'}>
					<span>Input title of Test</span>
						<TitleField
							required
							rows={2} 
							value={this.state.testTitle}
							onChange={(e) => {this.setState({testTitle: e.target.value})}}
						/>
					  
	          		</FlexContainer>
					  <FlexContainer width={100}>
						<FlexContainer dir='column' >
							Test type  
							{this.selectBox()}
						</FlexContainer>
						<FlexContainer dir='column' >
							Company name
							<Input 
								required
								type='text' 
								placeholder='Input company'
								value={this.state.company}
								onChange={(e) => {this.setState({company: e.target.value})}}
							/>
						</FlexContainer>
						<FlexContainer dir='column' >
							Test deadline
							<Input 
								required
								type='date' 
								placeholder='Input deadline of test'
								value={this.state.testDeadline}
								onChange={(e) => {this.setState({testDeadline: e.target.value})}}
								
							/>
						</FlexContainer>
						</FlexContainer>
	            </FlexContainer>
				</Div>
				<Div width={20}>
					{this.props.questions.length > 0 && <Button width={100}>Add Test</Button>} 	
				</Div>
			</form>
			<Div width={90}>
				
			</Div>
			<Div width={20}></Div>	
			<FlexContainer dir={'column'} width={100}>
				<FlexContainer width={100}>
					{this.props.questions.length > 0 && this.questionsListCreater()}
				</FlexContainer>
			</FlexContainer> */
		<div>
			<form onSubmit={this.submitHandler}>
				<TestCreaterLink> Home / My Account / Test Create </TestCreaterLink>
				<FlexRow width={'100%'}>
					<Title>
						<TestDetail 
							width={'calc(100% - 16px)'} 
							type="input"
							placeholder="Test Title"
							value={this.state.testTitle}
							onChange={(e) => {this.setState({testTitle: e.target.value})}} />
					</Title>
					<Date>
					<Date width={'calc(100% - 8px)'} type='date' placeholder="Deadline"/>
					</Date>
					<Type> {this.selectBox()}  </Type>
				</FlexRow>
				<FlexRow width={'100%'}>
					<Description placeholder="Description" />
				</FlexRow>
				<FlexRow width={'75.67%'}>
					<Minutes>
						<TestDetails width={'calc(100% - 16px)'} type='number' min="0"  placeholder="Minutes" />
					</Minutes>
					<Score>
						<TestDetails width={'calc(100% - 8px)'} type='number' min="0"  placeholder="Total Score" />
					</Score>
					<PassScore>
						<TestDetails width={'calc(100% - 8px)'} type='number' min="0"  placeholder="Passing Score" />
					</PassScore>
				</FlexRow>
				<AddButton>ADD QUESTION</AddButton>
				<QuestionCreater />
			</form>
		</div>
	);
}

}


function mapStateToProps(state) {
	console.log(state.test.questions);
  return {
	  questions: state.test.questions,
	}
}
function mapDispatchToProps(dispatch) {
  return {
	deleteQuestions: () => dispatch(deleteQuestions()),
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(TestCreater)
