import React, { Component } from 'react';
import styled, {css} from 'styled-components'
import { connect } from 'react-redux';
import {submittedFalse, submittedTrue,  increaseTotalScore} from '../../store/actions/testCreater';
import { firebase } from '../../firebase/firebase';
import axios from 'axios'


const Main = styled.div`
	margin: auto;
	max-width: 826px;
	font-size: 24px;
	overflow: auto;
`;
const FlexRow = styled.div`
	display: flex;
	position:relative;
	flex-direction: ${props => props.dir || 'row'};
	flex-wrap: wrap;
	margin: 30px 0;
	justify-content: space-between;
	align-items: center;
	width: ${props => props.width || '100%'};
	height: ${props => props.height || '100%'};
	box-sizing: border-box;

	@media screen and (max-width: 1190px) {
		flex-direction: column;
		min-width: 100%;
	}
`;
const FlexChild = styled.div`
	position:relative;
	width:  ${props => props.width || '25%'};
	box-sizing: border-box;
		   
	@media screen and (max-width: 1190px) {
		margin: 5px;
		min-width: 100%;
	}
`;
const QuestionNumber = styled.div`
	font-size: 24px;
	color: #100529;
	height: 44px;
	width: 100%;
	text-align: center;
	line-height: 44px;
	box-sizing: border-box;
`;
const Timer = styled.div`
	border: 1px solid  #4F9DA6;
	height: 44px;
	width: 100%;
	text-align: center;
	line-height: 44px;
	box-sizing: border-box;
`;
const AnswerTitle = styled.div`
	width: 100%;
	min-height: 100px;
	background-color: rgba(0, 0, 0, 0.5)
`;

class TestPassPanel extends Component {
	constructor(props) {
		super(props);
		this.state = {
			testData: {},
			choosed: false,
			testEnd: false,
			totalScore: 0,
			currentQuesion: {},
		}
    }
   componentDidMount() {	

	axios.get('https://test-project-4ab6b.firebaseio.com/tests/-LS0j3a0GongklJjoPEw.json')
		.then((response) => {
			console.log(response.data.questions)
			this.setState({testData: response.data});
		});		
	}

	componentDidUpdate(prevProps, prevState) {
		console.log(prevState, this.state);
	}

	render() {
		let questions = [];
		if (this.state.testData.questions) {
			questions = this.state.testData.questions;
		} else {
			questions = [];
		}
		return (
			
			<Main>
				<FlexRow >
					<FlexChild width={'80%'}>
						<QuestionNumber>Question 1/{questions.length}</QuestionNumber>
					</FlexChild>
					<FlexChild width={'144px'}>
						<Timer>22: 30</Timer>
					</FlexChild>
					{questions.map((question, index) => (
					<FlexRow>
						<FlexChild  width={'100%'}>
						 	<AnswerTitle>{question.answers[0].title}</AnswerTitle>
						</FlexChild>
					</FlexRow>
					))}
				</FlexRow>
			</Main>
		);
	}
}

function mapStateToProps(state) {
	return {
		totalScore: state.test.totalScore,
		submitted: state.test.submitted,
	}
}
function mapDispatchToProps(dispatch) {
	return {
	 submittedTrue: () => dispatch( submittedTrue()),
	 submittedFalse: () => dispatch( submittedFalse()),
	 increaseTotalScore: (score) => dispatch(increaseTotalScore(score)),
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(TestPassPanel)
