import React from 'react';
import OneAnswerCreater from './OneAnswerCreater';
import styled from 'styled-components'
import {connect} from 'react-redux';
import {createAnswers} from '../../../store/actions/createAnswer';

const Li = styled.li`
`;
const FlexContainer = styled.div`
    margin-top: 10px;
    display: flex;
    align-items: center;
    justify-content: space-between;
    width: ${props => props.width + 'px' || ''};
    flex-direction: ${props => props.dir || 'row'};
`;
const Button = styled.button`
  margin: 5px;
  padding: 2px;
  background: transparent;
  border-radius: 3px;
  border: 1px solid ${props => props.color  || 'grey'};
  background-color: ${props => props.color || 'white'};
  width: ${props => props.width + 'px' || ''};
  height:${props => props.height+ 'px' || ''};
`;

class QuestionCreater extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isRight: 0,
            answersInputCount: 2,
            answer: this.props.answers,
        };
    }
    componentDidMount() {
        this.generateArray();
        this.answersListCreater();
    }
    rightAnswerChangeHandler = (e) => {
        this.setState({isRight: +e.target.value});
    }
    generateArray = (prevState) => {
    	let newAnswer = {id: Date.now(), title: '', isRight: false}
        this.setState((prevState) => ({
            answers: prevState.answers.concat(newAnswer),
        }));

    }
    answersListCreater = () => {
        this.generateArray();
         return ( 
            <ul>
              {this.state.answers.map((input, index) => (
              	  <li>
                    <FlexContainer key={index + 1} width={600} >
                      <input type='radio' 
                         value={index + 1} 
                          name='rightAnswer' 
                          onChange={this.rightAnswerChangeHandler}
                      />
                      <OneAnswerCreater 
                        id={index + 1} 
                        isRight={index + 1 === this.state.isRight ? true : false}
                        getInputValue={this.getInputValue}
                        answers={this.ans}
                      />
                      <Button id={index + 1} width={50} onClick={this.answerDelete}> X </Button>
                    </FlexContainer>
                    </li>
               	))}
            </ul>
          )

    }   
    inputsCountChangeHandler = (e) => {
        console.log(e.target.value)
        return new Promise((resolve, reject) => {
            if (e.target.id === 'plus' && this.state.answersInputCount <= 4) {
                this.setState({answersInputCount: this.state.answersInputCount + 1});
            }
            if (e.target.id === 'minus' && this.state.answersInputCount >= 3) {
                this.setState({answersInputCount: this.state.answersInputCount - 1});
            }
            resolve(this.state.answersInputCount);
            })
            .then (number => {
                this.generateArray();
                this.answersListCreater();
                this.props.createAnswers(this.ans);
            })
    }
    answerDelete = (e) => {
        //if (e.target.id === )
    }

    render() {
        return (
            <div>
                <FlexContainer width={600} dir={'column'}>
                    <FlexContainer width={200}>
                        <span>Count of answers</span> 
                        <Button width={20} height={20} onClick={this. inputsCountChangeHandler} id='minus'> - </Button>
                            {this.state.answersInputCount}
                        <Button width={20} height={20} onClick={this. inputsCountChangeHandler} id='plus'> + </Button>
                    </FlexContainer>
                    {this.answersListCreater()}
                </FlexContainer>

            </div>
        );


    }
}
function mapStateToProps(state) {
  return {answers: state.quiz.answers}
}
function mapDispatchToProps(dispatch) {
  return {
    createAnswers: (answers) => dispatch(createAnswers(answers)),
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(QuestionCreater)
