import styled from 'styled-components';
import React from 'react';

const DescriptionDiv = styled.div`
		position: absolute;
        padding: 10px 10px;
        visibility: hidden;
        word-wrap: break-word;
        left: 0px;
		top: 0px;
        width: 100%;
        height: ${props => props.companyLogin === true ? '100%' : '80%'}
	    color: white;
		font-size: 20px;
		background-color: rgba(0, 0, 0, 0.7);
		border-radius: 4px;
		transition: top 0.5s ;

		@media screen and (max-width: 1188.5px) {
			font-size: 12px;
		}		
`;

const TestDescription = props => {
    return (
        <DescriptionDiv 
            companyLogin={props.companyLogin}
            onmouseOver={((e) => {e.target.style.visibility = 'visible'})}>
			{props.description}
		</DescriptionDiv >
	)
}

export default TestDescription;
