import React from 'react';
import appStore from '../../images/footerIcons/appst.png';
import googlePlay from '../../images/footerIcons/gog.png';
import callIcon from '../../images/footerIcons/callicon.png';
import styled from 'styled-components';

const FlexRow = styled.div`
    display: flex;
    max-width: 1200px;
    margin: auto;
    flex-direction: row;
    justify-content: ${props => props.content || 'space-between'};  ;
	flex-wrap: wrap;
    box-sizing: border-box;

	@media screen and (max-width: 1190px) {
		flex-direction: column;
        min-width: 100%;
    }
`;

const EmailDiv = styled.div`
    width: 100%;
    min-height: 100px;
    background-color: #141218;
    padding: 25px 0;

    @media screen and (max-width: 1190px) {
		padding: 25px 20px;
    }

`;

const Subscribe = styled.span`
    font-size: 34px;
    color: white;
`;

const EmailInput = styled.input`
    vertical-align: middle;
    min-width: 404px;
    height: 44px;
    padding-left: 16px;
    border: 1px solid #FF5959;
    font-size: 24px;

    @media screen and (max-width: 1190px) {
		flex-direction: column;
        min-width: 70%;
    }

`;
const EmailButton = styled.button`
    width: 44px;
    height: 44px;
    vertical-align: middle;
    background-color: #FF5959;
    border: 1px solid #FF5959;
    color: white;
    font-size: 36px;

 
 
`;


const Footer = () => {
    return (
        <footer className='footer'>
            <EmailDiv>
            <FlexRow>
                <Subscribe>SUBSCRIBE & GET <span style={{color: '#FFAD5A'}}>NEWS</span></Subscribe>
                <div><EmailInput type="email" placeholder='EMAIL' />< EmailButton>></ EmailButton></div>
            </FlexRow>
            </EmailDiv>


            <div className='footer_middle_wrapper'>
                <div className='footer_middle'>
                    <div className='contact_us'>
                        <div style={{
                            display: 'flex',
                            alignItems: 'center',
                        }}>
                            <div style={{

                                marginRight: '10px',
                                background: `url(${callIcon})`,
                                backgroundPosition: "center",
                                backgroundSize: 'cover',
                                width: '24px',
                                height: '24px',
                                padding: '5px',
                            }}>

                            </div>
                            <div>CONTACT US</div>
                        </div>
                        <div>Erevan, Kilikia, sisvan str. 59</div>
                        <div style={{paddingBottom: '10px'}}>+ 374 (94) 444444</div>
                        <div>info@digilearn.com</div>
                    </div>


                    <div className='support'>
                        <div style={{
                            display: 'flex',
                            alignItems: 'center',
                        }}>
                        <div style={{
                            width:'24px',
                            height:'24px',
                            border: '1px solid black',
                            borderRadius: '50%',
                            padding: '5px',
                            textAlign: 'center',
                            marginRight: '10px',
                        }}>?</div>
                            <div>SUPPORT</div>
                        </div>
                        <div className='services'>
                            <span>Customer Service</span>
                            <span>Privacy Policy</span>
                        </div>
                        <div>Contact Us</div>
                    </div>

                    <div className='about_digi'>
                        <div>
                        <span style={{
                            marginRight: '10px',
                            backgroundColor: '#FF5959',
                            padding: '5px',
                        }}>DL</span>
                            <span>ABOUT DIGILEARN</span>
                        </div>
                        <div className='services'>
                            <span>Company</span>
                            <span>Partners</span>
                        </div>
                        <div className='services'>
                            <span>About us</span>
                            <span>Mobile Apps</span>
                        </div>
                    </div>


                    <div className='down_buttons'>
                        <div style={{
                            backgroundImage: `url(${googlePlay})`,
                            backgroundSize: 'cover',
                            width: '170px',
                            height: '50px',
                            border: '1px solid #00253C',
                            borderRadius: '4px',
                            marginBottom: '26px',
                        }}/>
                        <div style={{
                            backgroundImage: `url(${appStore})`,
                            backgroundSize: 'cover',
                            width: '170px',
                            height: '50px',
                            border: '1px solid #00253C',
                            borderRadius: '4px',
                        }}/>
                    </div>

                </div>
            </div>


            <div className='footer_bottom'>
                <button>iji</button>
                <button>iji</button>
                <button>iji</button>
                <button>iji</button>
                <button>iji</button>
                <button>iji</button>
                <button>iji</button>
                <button>iji</button>
            </div>
        </footer>
    );
};

export default Footer;
