import React, { Component } from 'react';
import styled, {css} from 'styled-components'
import { connect } from 'react-redux';
import forUserImg from './img/forUser.jpg';
import CheckBox from '../../components/HomePage/CheckBox';

const GetStartedDiv = styled.div`
    position: absolute;
    bottom:0;
    color: white;
    width: 100%;   
    height: ${props => props.height || '100px'};  
    z-index: 1;
    background-color: rgba(20, 18, 24, 0.75);
    font-size: 24px;	
`;

const FlexRow = styled.div`
    display: flex;
    margin: 19px auto;
    max-width: 100%;
    flex-direction: row;
    justify-content: ${props => props.content || 'space-between'};  ;
	flex-wrap: wrap;
    box-sizing: border-box;

	@media screen and (max-width: 1190px) {
		flex-direction: column;
		min-width: 100%;
	}
`;

const FlexChild = styled.div`
	position: relative;
	width: ${props => props.width || ''};  
    box-sizing: border-box; 
    
	@media screen and (max-width: 1190px) {
		margin: 20px 5px;
		min-width: 98%;
	}
`;

const Button = styled.button`
    margin: 0 16px;
    width: ${props => props.width || ''};  
    height: 60px;
    border: 0;
    border-radius: 4px;
    padding: 16px;
    background-color:rgba(255, 89, 89, 1);
    color: white;
    font-weight: bold;
    font-size: 18px;
    box-sizing: border-box;
`;

const TitleWhite = styled.div`
    padding: 100px 16px 16px 16px;
    color: white;
    font-size: 48px;
    font-weight: bold;
    box-sizing: border-box;
   
`;
const BlackShadow = styled.div`
    position: absolute;
    width: 588px;
    height: 500px;
    background-color: rgba(20, 18, 24, 0.5);
`;
const Block = styled.div`
    position: relative;
    width: 588px;
    height: 500px;
    background-image: url(${forUserImg});
    background-position: center; 
    background-repeat: no-repeat; 
    background-size: cover;
    box-sizing: border-box;
`;

class BecomeUser extends Component {
	constructor(props) {
		super(props);
		this.state ={};
	}

    render() {
		return (
            <FlexChild>
                    <Block>
                        <BlackShadow>
                            <TitleWhite>
                                FOR USER  
                            </TitleWhite>
                            <CheckBox>Complete Tests</CheckBox>
                            <CheckBox>Complete Tests</CheckBox>
                            <CheckBox>Recieve Invitations from Leading Companies</CheckBox>
                                                                
                            <GetStartedDiv>
                            <FlexRow content={'flex-end'}>
                                <Button width={'228px'}>
                                    BECOME USER
                                </Button>
                            </FlexRow>
                            </GetStartedDiv>
                        </BlackShadow>
                    </Block>
            </FlexChild>
               
        );
	}
}

function mapStateToProps(state) {
	return {
	
	}
}

export default connect(mapStateToProps, null)(BecomeUser)
