import React, { Component } from 'react';
import styled, {css} from 'styled-components'
import { connect } from 'react-redux';
import 'react-sticky-header/styles.css';
import { StickyContainer, Sticky } from 'react-sticky';
import TestTitle from '../../components/QuizCreator/TestInputs/TestTitle';
import TestCreatorCompany from '../../components/QuizCreator/TestInputs/TestCreatorCompany';
import TestType from '../../components/QuizCreator/TestInputs/TestType';
import TestDeadline from '../../components/QuizCreator/TestInputs/TestDeadline';
import TestDuration from '../../components/QuizCreator/TestInputs/TestDuration';
import TestDescription from '../../components/QuizCreator/TestInputs/TestDescription';
import Button from '../../components/QuizCreator/Button';
import QuestionCreator from './QuestionCreator/QuestionCreator';

const Main = styled.div`
	margin: auto;
	max-width: 1200px;
	font-size: 24px;
	overflow: auto;
`;

const FlexRow = styled.div`
	display: flex;
	position: relative;
	flex-direction: row;
	flex-wrap: wrap;
	margin: 30px 0;
	justify-content: space-between;
	width: ${props => props.width || '100%'};
	box-sizing: border-box;

	@media screen and (max-width: 1190px) {
		flex-direction: column;
		min-width: 100%;
	}
`;
const QuestionsDiv = styled.div`
	border-bottom: 1px solid #D6D6D6;
`;

class TestHeader extends Component {
	constructor(props) {
		super(props);
		this.state = {
			testTitle: '',
			description: '',
			testDeadline: '',
			testType: '',
			company: '',
            testDuration: '',
            submitted: false,
            questions: [],
            
		}
	}
    getInputValue = (e, field) => {
        let value = e.target.value;
        value = this.clearWordFromSpaces(value);
        
        if (e.target.type === 'number') {
            if (value < 0) {
                value = +value * (-1);
            } else if (value > 0) {
                value = +value;
            } else {
                value = '';
            }
        }
        this.setState({ [field]: value });
    }

    clearWordFromSpaces = (word) => {
		return word.replace(/^[ ]+/g, '').replace(/\s*$/, '');
    }
    
    checkInputValidation = (e) => {
        let placeholder = '';
        let invalidPlaceholder = '';

        if (e.target.placeholder) {
            placeholder =  e.target.placeholder
            invalidPlaceholder = `Fill ${placeholder}`;
        }
        
        e.target.placeholder = !e.target.value 
            ? invalidPlaceholder 
            : placeholder.replace('Fill ','');
    }
    
    isFilled = (inputValue) => {
        console.log(inputValue);
        return !inputValue && this.state.submitted ? true : false;
    }
    
    addQuestion = () => {
        this.setState({submitted: true});
        if (this.formValidation) {
            let oneQuestion = {
                id: Date.now(),
                answers: [{ id: Date.now(), title: '', isRight: false }, { id: Date.now() + 1, title: '', isRight: false }],
                isRight: 0,
                questionTitle: '',
                score: '',
        }
            this.setState({ questions: this.state.questions.concat(oneQuestion), testCreated: false});
        }
    }
    
	formValidation = () => {
		let state = this.state;
		return state.testTitle 
				&& state.description
				&& state.testDeadline 
				&& state.description 
				&& state.company 
				&& state.testDuration 
				&& state.testType 
	}
    
	componentDidUpdate(prevProps, prevState) {
	
	}
	
	render() {
		return (
            <Main>
                <FlexRow>
                    <TestTitle
                        validation={this.checkInputValidation}
                        getInputValue={this.getInputValue}
                        value={this.state.testTitle}
                        isFilled={this.isFilled}
                    />
                    <TestCreatorCompany 
                        validation={this.checkInputValidation}
                        getInputValue={this.getInputValue}
                        value={this.state.company}
                        isFilled={this.isFilled} 
                    />
                    <TestType 
                        validation={this.checkInputValidation}
                        getInputValue={this.getInputValue}
                        value={this.state.testType}
                        isFilled={this.isFilled} 
                    />
                </FlexRow>
                <FlexRow>
                    <TestDescription 
                        validation={this.checkInputValidation}
                        getInputValue={this.getInputValue}
                        value={this.state.description}
                        isFilled={this.isFilled}
                    />
                </FlexRow>
                <FlexRow>
                    <TestDeadline 
                        validation={this.checkInputValidation}
                        getInputValue={this.getInputValue}
                        value={this.state.testDeadline}
                        isFilled={this.isFilled} 
                    />
                    <TestDuration
                        validation={this.checkInputValidation}
                        getInputValue={this.getInputValue}
                        value={this.state.testDuration}
                        isFilled={this.isFilled}  
                    />
                   <Button onClick={this.addQuestion}>ADD QUESTION</Button>
                </FlexRow>
                {this.state.questions.length > 0 && 
							<QuestionsDiv>
								{this.state.questions.map((question, index) =>
									<QuestionCreator
										isAnswerValid={this.isAnswerValid}
										isQuestionValid={this.isQuestionValid}
										key={question.id + index}
										id={question.id}
										count={index + 1}
										getQuestionValues={this.getQuestionValues} 
										deleteQuestion={this.deleteQuestion}
									/>
								)}
							</QuestionsDiv>
						}
            </Main>
		);
	}
}

function mapStateToProps(state) {
	return {
		totalScore: state.test.totalScore,
		submitted: state.test.submitted,
	}
}

function mapDispatchToProps(dispatch) {
	return {
		
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(TestHeader)
