import {ADD_ANSWER, SET_ANSWERS_VALUES, DECREASE_ANSWERS_COUNT, DELETE_CHOSEN_ANSWER} from '../actions/actionTypes'

const initialState = {
  answers: [{id: Date.now(), title: '', isRight: false}, {id: Date.now()+1, title: '', isRight: false}],
  answersInputCount: 2,
}

export default function quizReducer(state = initialState, action) {
  switch (action.type) {
    case ADD_ANSWER:
      return {
        ...state,
        answers: state.answers.concat(action.answer),
        answersInputCount: state.answersInputCount + 1,
      }
      case SET_ANSWERS_VALUES:
      return {
        ...state,
        answers : action.answers
      }
      case DECREASE_ANSWERS_COUNT:
      return {
        ...state,
        answers : state.answers.slice(1),
        answersInputCount: state.answersInputCount - 1,

      }
      case DELETE_CHOSEN_ANSWER:
      return {
        ...state,
        answers : state.answers.slice(1, action.id),
        answersInputCount: state.answersInputCount - 1,
      }

      default:
      return state
  }
}
