import React, { Component } from 'react';
import styled from 'styled-components';
import QuestionCreater from './QuestionCreater/QuestionCreater';

const Input = styled.input`
	margin: 10px;
	padding: 10px 5px;
	width: 80%;
	height: 10px;
	font-size: 12px;
`;
const FlexContainer = styled.div`
  display: flex;
  margin: auto;
  padding: 0 10px;
  margin-top: 1em;
  font-size: 14px;
  align-items: center;
  justify-content: space-between;
  width: ${props => props.width + '%' || '100%'};
  flex-direction: ${props => props.dir || 'row'};
  box-sizing: border-box;
`;
const Button = styled.button`
	margin: 5px;
	font-size: 18px;
  background: transparent;
  border-radius: 5px;
  border: 1px solid ${props => props.color  || ''};
  background-color: ${props => props.color || 'white'};
  width: ${props => props.width + 'em' || '2em'};
  height:${props => props.height + 'px' || '10px'};
`;
const TitleField = styled.textarea`
  rows: ${props => props.rows  || '4'};
  width: 100%;
`;

class TestCreater extends  Component {
	constructor(props) {
		super(props);
	}
	render() {
		return(
			<div>
			<FlexContainer width= {45} dir={'column'}>
				<FlexContainer dir={'column'} width={100}>
	          		<span>Input title of Test</span>
	          		<TitleField
	          			required
	          			rows={3} 
	          			value={''}
	          			id='title'
	          		/> 
	          	</FlexContainer>
	          	<FlexContainer>
	          		<FlexContainer dir='column' >
	          			Test type  
	          			<Input 
	          				required
	          				type='text' 
	          				placeholder='input type JS, PHP...'
	          		
	          		/>
	          	 	</FlexContainer>
	          		<FlexContainer dir='column' >
	          			Company name
	          			<Input 
	          				required
	          				type='text' 
	          				placeholder='Input company'
	          			/>
	          	 	</FlexContainer>
	          	 	<FlexContainer dir='column' >
	          			Test deadline
	          			<Input 
	          				required
	          				type='date' 
	          				placeholder='Input deadline of test'
	          			/>
	          	 	</FlexContainer>
	          	</FlexContainer>
	          	
			</FlexContainer>
			<QuestionCreater />
		</div>
		
	);

	}
}

export default TestCreater;
