import React, { Component } from 'react';
import OneAnswerCreator from './OneAnswerCreator';
import RadioFlag from '../../../components/QuizCreator/RadioFlag';
import styled, {css} from 'styled-components'
import {connect} from 'react-redux';
import {changeTotalScore, submittedFalse} from '../../../store/actions/testCreator';
import DeleteQuestion from '../QuestionCreator/QuestionComponents/DeleteQuestion';
import QuestionScore from './QuestionComponents/QuestionScore';

const Radio = styled.input`
	margin: 16px 5px;
	width: 22px;
	height: 22px;

	:before {
		width: 21px;
		height: 21px;
		border-radius: 15px;
		top: -1px;
		left: -1px;
		position: relative;
		background-color: white;
		content: '';
		display: inline-block;
		visibility: visible;
		border: 1px solid rgba(79, 157, 166, 1);
	}
	:checked:after {
		width: 11px;
		height: 11px;
		border-radius: 15px;
		top: -22px;
		left: 4px;
		position: relative;
		background-color: rgba(79, 157, 166, 1);
		content: '';
		display: inline-block;
		visibility: visible;
		border: 1px solid rgba(79, 157, 166, 1);
	}
`;

const DeleteAnswer = styled.button`
		margin: 8px 8px;
		width: 40px;
		height: 40px;
		color:rgba(230, 36, 22, 1);
		font-size: 24px;
		background-color: white;
		border: 1px solid rgba(231, 231, 231, 1);
`;
const QuestionDetails = styled.textarea`
	padding: 16px 16px;
	width: ${props => props.width  || 'calc(100% - 16px)'};
	height: 60px;
	padding-left: 16px;
	color: #4F9DA6;
	font-size: 24px;
	overflow: hidden; 
	border: 1px solid #4F9DA6;
	box-sizing: border-box; 
	transition: font-size 0.5s ease-in-out;
	
	@media screen and (max-width: 1190px) {
		margin: 10px 5px;
		max-width: 99%;
	}
	@media screen and (max-width: 580px) {
		font-size: 12px;
	}
  ::-webkit-input-placeholder {color: rgba(79, 157, 166, 0.5)}
	::-moz-placeholder          {color:rgba(79, 157, 166, 0.5)}/* Firefox 19+ */
	:-moz-placeholder           {color:rgba(79, 157, 166, 0.5)}/* Firefox 18- */
	:-ms-input-placeholder      {color:rgba(79, 157, 166, 0.5)}

	${props => props.invalid && css`
		font-size: 22px;
		color: rgba(185, 4, 46, 0.5);
		border-bottom: 1px solid rgba(185, 4, 46, 1);
		::-webkit-input-placeholder {color: rgba(185, 4, 46, 0.5)}
		::-moz-placeholder          {color: rgba(185, 4, 46, 0.5)}/* Firefox 19+ */
		:-moz-placeholder           {color: rgba(185, 4, 46, 0.5)}/* Firefox 18- */
		:-ms-input-placeholder      {color: rgba(185, 4, 46, 0.5)}
  `}
`;

const AddAnswerDiv = styled.div`
	font-size: 14px;
	color: #4F9DA6;
`;
const ButtonsDiv = styled.div`
	display: flex;
	width: 132px;
`;
const ButtonsFlexChild = styled.div`
	flex:44px; 
	height: 44px; 
	line-height: 44px; 
	text-align: center; 
	border: 1px solid #4F9DA6;
`;
const CountFlexChild = styled.div`
	flex:44px; 
	height: 44px; 
	line-height: 44px; 
	text-align: center; 
	border: 1px solid #4F9DA6;
	border-right: 0;
	border-left: 0;
`;
const Question = styled.div`
	margin: 30px 0;
	position: relative;
`;
const FlexRow = styled.div`
	display: flex;
	flex-direction: row;
	align-items: flex-start;
	margin: 15px 0;
	width: ${props => props.width || '100%'};
	box-sizing: border-box;

	@media screen and (max-width: 1190px) {
		flex-wrap:  ${props => props.wrap || 'wrap'};
		min-width: 100%;
		max-width: 100%;
	}
`;
const FlexChild = styled.div`
	max-width:  ${props => props.maxWidth || '100%'};
	width: ${props => props.width || '100%'};
	box-sizing: border-box;
	@media screen and (max-width: 1190px) {
		min-width: 99%;
	}
`;
class QuestionCreator extends Component {
   	constructor(props) {
			super(props);
  		 this.state = {
				id: this.props.id,
				answers: [{id: Date.now(), title: ''}, {id: Date.now() + 1, title: '',}],
				isRight: 0,
				questionTitle: '',
				score: '',
				
			}
			
	}

	getInputValue = (e, field) => {
        let value = e.target.value;
        value = this.clearWordFromSpaces(value);
        if (e.target.type === 'number') {
            if (value < 0) {
                value = +value * (-1);
            } else if (value > 0) {
                value = +value;
            } else {
                value = '';
            }
        }
        this.setState({ [field]: value });
    }

    clearWordFromSpaces = (word) => {
		return word.replace(/^[ ]+/g, '').replace(/\s*$/, '');
    }
    
    checkInputValidation = (e) => {
        let placeholder = '';
        let invalidPlaceholder = '';

        if (e.target.placeholder) {
            placeholder =  e.target.placeholder
            invalidPlaceholder = `Fill ${placeholder}`;
        }
        
        e.target.placeholder = !e.target.value 
            ? invalidPlaceholder 
            : placeholder.replace('Fill ','');
    }
    
    isFilled = (inputValue) => {
        return !inputValue && this.props.submitted ? true : false;
    }

	
	answersCountHandler =(actionName, id = '') => {
		if (actionName === 'minus') {
			if (this.state.answers.length > 2) {
				this.setState({answers: this.state.answers.splice(1)});
			} 
		}
		if (actionName === 'plus') {
			let newAnswer = {id: Date.now(), title: ''};
			if (this.state.answers.length < 5) {
				this.setState({answers: this.state.answers.concat(newAnswer)});
			}
		}
		if (actionName === 'delete') {
			if (this.state.answers.length > 2) {
				this.setState( {answers: this.state.answers.filter(answer => answer.id !== id) });
				this.state.isRight === id && this.setState({isRight: 0});
			}
		}
	}

	updateQuestionValues = () => {
		
		this.getInputValue();
		let state = {
			questionTitle: '',
			score: '',
			isRight: '',
			answers: [],
		}
		this.isQuestionValid();
		this.props.getQuestionValues(this.props.id, state);
	}

	
	isQuestionValid = () => {
		if (this.state.isRight && this.state.questionTitle && this.state.score) {
			this.props.isQuestionValid(true)

		} else {
			this.props.isQuestionValid(false);
		}
	}

	componentDidUpdate(prevProps, prevState) {
		
		
	}

	answersListCreator = () => {
		return ( 
			this.state.answers.map((input, index) => (
				<FlexRow key={input.id} width={'100%'}>
					<Radio
						type='radio'
						name={`question${this.props.id}`} 
						value={input.id} 
						onClick={(e) => this.setState({isRight: +e.target.value})}
					/>
					<OneAnswerCreator
						isAnswerValid={this.props.isAnswerValid}
						valid={this.props.answerValid}
						count={index + 1}
						id = {input.id}
						getInputValue={this.getInputValue}
					/>
					{this.state.answers.length > 2 && 
					<DeleteAnswer 
						type='button'
						id='delete'
						onClick={(e) => this.answersCountHandler(e, input.id)}

					>
						X 
					</DeleteAnswer>}
				</FlexRow>))
		);
	} 	  
    render() {
      return (
		<Question>
			<FlexRow width={'98%'}>
				<FlexChild width={'1068px'}>
					
					{!this.state.isRight && this.props.submitted && <RadioFlag count={this.props.count}/>}
					<DeleteQuestion 
						score={this.state.score}
						id={this.props.id}
						count={this.props.count}
					/>

					<QuestionDetails
						type='text' 
						placeholder={this.checkInputValidation('title')}
						value={this.state.questionTitle}
						invalid = {this.isFilled (this.state.questionTitle)}
						onChange={(e) => this.setState({questionTitle: e.target.value})} 
					/>
					</FlexChild>
					<QuestionScore 
						validation={this.checkInputValidation}
                        getInputValue={this.getInputValue}
                        value={this.state.score}
                        isFilled={this.isFilled} 
					/>
				</FlexRow>
				<FlexRow width={'98%'} wrap={'wrap-reverse'}>
					<FlexChild maxWidth={'1060px'}>
						{this.answersListCreator()}     
					</FlexChild>
					<FlexChild width={'140px'} verAlign={'top'} >
						<AddAnswerDiv>Add more answers</AddAnswerDiv>
						<ButtonsDiv>
							<ButtonsFlexChild 
								type='button'
								id='minus'
								onClick={this.answersCountHandler}>
									-
							</ButtonsFlexChild>							
							<CountFlexChild>{this.state.answers.length}</CountFlexChild>
							<ButtonsFlexChild 
								type='button'
								id='plus'
								onClick={this.answersCountHandler}>
									+
							</ButtonsFlexChild>
						</ButtonsDiv>
					</FlexChild>						
				</FlexRow>			
        	</Question>
	    );
  	}
}
function mapStateToProps(state) {
  return {
	 totalScore: state.testCreator.totalScore,
  }
}
function mapDispatchToProps(dispatch) {
  return {
		changeTotalScore: (score) => dispatch(changeTotalScore(score)),
		
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(QuestionCreator)
