import {INCREASE_TOTAL_SCORE, SUBMITTED} from '../actions/actionTypes';

const initialState = {
  questions: [],
  totalScore: 0,
  isValid: false,
  submitted: false,
}

export default function testReducer(state = initialState, action) {
  switch (action.type) {
    case INCREASE_TOTAL_SCORE:
      return {
        ...state,
        totalScore: state.totalScore + action.score,
      }
    case SUBMITTED:
      return {
        ...state,
        submitted: action,
      }
    default:
      return state
    
  }
  
}
