import {getCompaniesStarted, 
        getCompaniesSuccess, 
        getCompaniesFailure,
        getTestsStarted, 
        getTestsFailure, 
        getTestsSuccess,
        getUsersStarted,
        getUsersFailure,
        getUsersSuccess,
    } from '../actions/appAction';
import {firebase} from '../../firebase/firebase';

export const getTests = () => {
    return dispatch => {
        dispatch(getTestsStarted());
        
        firebase.database().ref('tests').on('value', (snapshot) => {
	       let tests = [];
	        snapshot.forEach(childSnapshot => {
	            tests.push({
	                id: childSnapshot.key,
	                ...childSnapshot.val()
                }) 
               
            });
            dispatch(getTestsSuccess(tests));
        });
    }  
};

export const getCompanies = () => {
    return dispatch => {
        dispatch(getCompaniesStarted());
  
      fetch('https://test-project-4ab6b.firebaseio.com/companies.json')
        .then(response => response.json())
        .then(response => {
            let companies = Object.values(response);
            dispatch(getCompaniesSuccess(companies));
        })
        .catch(err => {
                dispatch(getCompaniesFailure(err.message));
        });
    };
  };

export const getUsers = () => {
    return dispatch => {
        dispatch(getUsersStarted());
        fetch('https://test-project-4ab6b.firebaseio.com/user.json')
        .then(response => response.json())
        .then(response => {
            let users = Object.values(response);
            dispatch(getUsersSuccess(users));
        })
        .catch(err => {
          dispatch(getUsersFailure(err.message));
        });
    };
  };
