import React, { Component } from 'react';
import OneAnswerCreater from './OneAnswerCreater';
import styled from 'styled-components'
import {connect} from 'react-redux';
import {addAnswer, decreaseAnswersCount, deleteChosenAnswer, rightAnswerChosen, 
	questionTitleHandler, scoreHandler, timeHandler, clearFields} from '../../../store/actions/questionCreater';
import {addQuestion} from '../../../store/actions/testCreater';

const RadioButton = styled.input`
		width:15px;
		height:15px;
`;
const Input = styled.input`
	margin: 5px;
	padding: 10px 5px;
	width: 80%;
	height: 10px;
	font-size: 12px;
`;

const Ul = styled.ul`
	margin:0;
	padding: 0;
	list-style: none;
	width: 100%;
`;
const Li = styled.li`

`;
const FlexContainer = styled.div`
	display: flex;
	margin: auto;
	padding: 0 10px;
  margin-top: 5px;
  font-size: 14px;
  align-items: center;
  justify-content: space-between;
  width: ${props => props.width + '%' || '100%'};
  flex-direction: ${props => props.dir || 'row'};
  box-sizing: border-box;
`;
const Button = styled.button`
	margin: 5px;
	font-size: 18px;
  background: transparent;
  border-radius: 5px;
  border: 1px solid ${props => props.color  || ''};
  background-color: ${props => props.color || 'white'};
  width: ${props => props.width + 'em' || '2em'};
  height:${props => props.height + 'px' || '10px'};
`;
const TitleField = styled.textarea`
  rows: ${props => props.rows  || '4'};
  width: 100%;
`;
class QuestionCreater extends Component {
   	constructor(props) {
			super(props);
  		 this.state = {
				answers: [{id: Date.now(), title: '', isRight: false}, {id: Date.now() + 1, title: '', isRight: false}],
				isRight: 0,
				questionTitle: '',
				time: '',
				score: '',
				isEditing: false,
			 }
		}
	componentDidMount() {
    this.answersListCreater();
  }
  addAnswer = () => {
   	let newAnswer = {id: Date.now(), title: '', isRight: false}
    this.setState({answers: this.state.answers.concat(newAnswer)});
  }
  isValid = (e) => {
  	switch (e.target.id) {
  		case 'score':
  			return	!e.target.value || e.target.value === 0 || e.target.setCustomValidity('Please input score of question');
  		case 'minutes':
  			return !e.target.value || e.target.value === 0 || 'Please input duration of question';
  		case 'title':
  			return !e.target.value || 'Please input title of question';
  		case 'radio':
  			return !e.target.value || 'Please input right answer of question';
  		default:
  			return !e.target.value || 'Please input answer';
  	}
	}
  answersListCreater = () => {
      return ( 
        <Ul>
          {this.state.answers.map(input => (
            <Li key={input.id}>
            	<FlexContainer >
	              <RadioButton
	              	required
	              	type='radio'
	              	name='isRight' 
	                value={input.id} 
	                onChange={(e) => this.setState({isRight: e.target.value})}
	              />
	              <OneAnswerCreater
	                id={input.id} 
	                isRight={input.id === this.state.isRight ? true : false}
	              />
	              <Button 
	              	width={2} 
	              	onClick={this.deleteChosenAnswer(input.id)}
	              	type='button'
	              > 
	              	X 
	              </Button>
	            </FlexContainer>
            </Li>))}
        </Ul>
      )
  }   
  inputsCountChangeHandler = (e) => {
      e.target.id === 'plus' && this.state.answers.length <= 4 && this.addAnswer();
   		e.target.id === 'minus' && this.state.answers.length >= 3 && this.setState({answers: this.state.answers.splice(1)});
      this.answersListCreater();
  }
  deleteChosenAnswer = (id) => {
   	for (let i = 0; i < this.state.answers.length; i++) {
		  this.state.answers.length > 2	&& this.setState({answers: this.state.answers.filter(answer => answer.id !== id)});
     console.log(id);
    }
  }
  submitHandler = (e) => {
  	e.preventDefault();
  	this.addQuestion();
  	this.clearFields();
  }
  clearWordFromSpaces = (word) => {
		return  word.replace(/^[ ]+/g, '').replace(/\s*$/, '');
	}
	addQuestion = () => {
			this.props.addQuestion(this.props.state);
	}
	clearFields = () => {
		let answers = this.props.answers.map((answer, index) => {
			answer.id = Date.now() + 1;
			answer.title = '';
			answer.isRight = false;
			return answer;
		})
		let newState = {
			answers : answers,
			isRight: 0,
		  questionTitle: '',
		  time: '',
		  score: '',
		  isEditing: false,
		}
	}
    render() {
      return (
        <div>
          <FlexContainer dir={'column'} width={100}>
          	<form onSubmit={this.submitHandler}>
							<FlexContainer dir={'column'}>
								<span>Input Question</span>
								<TitleField
									required
									rows={2} 
									value={this.state.title}
									id='title'
									onChange={(e) => (this.setState( {title: this.clearWordFromSpaces(e.target.value)}))}
								/> 
							</FlexContainer>
          	<FlexContainer>
          		<FlexContainer dir='column' >
          			Input score of question
          			<Input 
          				required
          				type='number' 
          				min='0'
          				id='score'
          				placeholder='Input score of question'
          				value={this.state.score}
          				onChange={(e) => (this.setState( {score: +e.target.value}))}

          		/>
          	 	</FlexContainer>
          		<FlexContainer dir='column' >
          			Input duration in minutes
          			<Input 
          				required
          				type='number' 
          				min='0'
          				id='minutes'  
          				placeholder='Input ending time of question'
          				value={this.state.time}
          				onChange={(e) => (this.setState( {time: +e.target.value}))}
          			/>
          	 	</FlexContainer>
          	</FlexContainer>
            <FlexContainer dir={'column'}>
	            <FlexContainer >
	              <span>Count of answers</span> 
	                <Button type='button' width={2} height={30} onClick={this. inputsCountChangeHandler} id='minus'> - </Button>
	                  {this.state.answers.length}
	                <Button type='button' width={2} height={30} onClick={this. inputsCountChangeHandler} id='plus'> + </Button>
	            </FlexContainer>
           	{this.answersListCreater()}

            <FlexContainer>
		        <Button width={10}> Add answer </Button>
		        </FlexContainer>
		        </FlexContainer>
		      </form>
        </FlexContainer>
		    </div>
      );
    }
}

function mapStateToProps(state) {
  return {
   	questions: state.test.questions,
  }
}
function mapDispatchToProps(dispatch) {
  return {
   	addQuestion: (question) => dispatch(addQuestion(question)),
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(QuestionCreater)
