import React, { Component } from 'react';
import styled, {css} from 'styled-components'
import { connect } from 'react-redux';
import {submittedFalse, submittedTrue,  increaseTotalScore} from '../../store/actions/testCreater';
import { firebase } from '../../firebase/firebase';


const Main = styled.div`
	margin: auto;
	max-width: 826px;
	font-size: 24px;
	overflow: auto;
`;
const FlexRow = styled.div`
	display: flex;
	position:relative;
	flex-direction: column;
	flex-wrap: wrap;
	margin: 30px 0;
	justify-content: space-between;
	align-items: center;
	width: ${props => props.width || '100%'};
	box-sizing: border-box;

	@media screen and (max-width: 1190px) {
		flex-direction: column;
		min-width: 100%;
	}
`;
const FlexChild = styled.div`
	position:relative;
	width:  ${props => props.width || '25%'};
	box-sizing: border-box;
		   
	@media screen and (max-width: 1190px) {
		margin: 5px;
		min-width: 100%;
	}
`;
const QuestionNumber = styled.span`
	font-size: 24px;
	color: #100529;
`;

class TestPassPanel extends Component {
	constructor(props) {
		super(props);
		this.state = {
            testData: {},
			choosed: false,
			testEnd: false,
			totalScore: 0,
			currentQuesion: {},
		}
    }
   componentDidMount() {	
		
		return new Promise((resolve, reject) => {
			let questions;
			firebase.database().ref('tests/-LS0j3a0GongklJjoPEw/questions').on('value', function(snapshot) {
				snapshot.forEach(childSnapshot => {
					questions.push({
						id: childSnapshot.key,
						...childSnapshot.val()
					})
				});
			})
		}).then(questions => this.setState({questions: questions}))
		console.log(this.state.questions)
	}
	render() {
		//let test = {...this.state.testData.data};
		let questions = this.state.questions.map(question => {
			return {...question}
		})
		console.log(questions)
		
		console.log()
		return (
			<Main>
				{
				questions.map(question => (
					<FlexRow>
				 		<QuestionNumber>Question 1/10</QuestionNumber>
				 		<FlexChild></FlexChild>
				 	</FlexRow>
				 	))
				}
			</Main>
		);
	}
}

function mapStateToProps(state) {
	return {
		totalScore: state.test.totalScore,
		submitted: state.test.submitted,
	}
}
function mapDispatchToProps(dispatch) {
	return {
	 submittedTrue: () => dispatch( submittedTrue()),
	 submittedFalse: () => dispatch( submittedFalse()),
	 increaseTotalScore: (score) => dispatch(increaseTotalScore(score)),
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(TestPassPanel)
