import React, { Component } from 'react';
import styled, {css} from 'styled-components'
import { connect } from 'react-redux';
import img from './img/rawpixel-558596-unsplash.jpg'

const Main = styled.div`
	margin: auto;
	max-width: 1920px;
	font-size: 24px;
	overflow: auto;
`; 
const Header = styled.div`
    position: relative;
    width: 100%;   
    height: 700px;		
    background-image: url(${img});
    background-position: center; 
    background-repeat: no-repeat; 
    background-size: cover;
    box-sizing: border-box;
`;

const GetStartedDiv = styled.div`
    position: absolute;
    bottom:0;
    color: white;
    width: 100%;   
    height: 100px;	
    z-index: 1;
    background-color: rgba(20, 18, 24, 0.75);
    text-align: center;
    font-size: 24px;	
`;
const GetsStartedText = styled.span`
    display: inline-block;
    width: 1018px;
    word-wrap: break-word;
    lign-height: 50px;
    box-sizing: border-box;   
`;

class HomePage extends Component {
	constructor(props) {
		super(props);
		this.state ={};
	}

	render() {
		return (
			<Main>
                <Header>
                    <GetStartedDiv>
                        <GetsStartedText>
                            DigiLearn is an all-in-one platform 
                            that makes it easy to create online tests, 
                            launch programming companies, build landing pages, 
                            and design the perfect website.
                        </GetsStartedText>
                    </GetStartedDiv>
                </Header>
            </Main>
		);
	}
}

function mapStateToProps(state) {
	return {
	
	}
}

export default connect(mapStateToProps, null)(HomePage)
