import React from 'react';
import ReactDOM from 'react-dom';
import CodeExample from '../../../components/code-example';
import { Dropdown, Button, Row, Col, Select } from 'antd';
import 'antd/dist/antd.css';

class DropdownPage extends React.Component {
  constructor() {
    super();

    this.state = {
      visible: false
    };
  }

  handleDropdownVisibility = val => {
    this.setState({
      visible: val
    });
  };

  render() {
    const menuTagSelect = () => {
      let children = [];

      for (let i = 10; i < 36; i++) {
        children.push(
          <Select.Option key={i.toString(36) + i}>
            {i.toString(36) + i}
          </Select.Option>
        );
      }

      return (
        <div className="m-dropdown-menu">
          <div>Match All:</div>
          <Select
            mode="tags"
            style={{ width: '100%' }}
            placeholder="Tags Mode"
            getPopupContainer={node => node.parentNode}
          >
            {children}
          </Select>
        </div>
      );
    };

    return (
      <Dropdown
        trigger={['click']}
        overlay={menuTagSelect()}
        visible={this.state.visible}
        onVisibleChange={val => this.handleDropdownVisibility(val)}
      >
        <Button>Dropdown with tag select</Button>
      </Dropdown>
    );
  }
}

ReactDOM.render(React.createElement(DropdownPage, null), document.getElementById('root'));
