import React from 'react';
import ContuctUsSvg from './ContactUsSvg';
import styled from 'styled-components';

const FlexRow = styled.div`
    display: flex;
    max-width: 1200px;
    margin: auto;
    flex-direction: ${props => props.dir || 'row'};
    justify-content: ${props => props.content || 'space-between'}; 
	flex-wrap: wrap;
    box-sizing: border-box;

	@media screen and (max-width: 1190px) {
		flex-direction: column;
        min-width: 100%;
    }
`;

const EmailDiv = styled.div`
    width: 100%;
    min-height: 100px;
    background-color: #141218;
    padding: 25px 0;

    @media screen and (max-width: 1190px) {
		padding: 25px 20px;
    }

`;

const Subscribe = styled.span`
    font-size: 34px;
    color: white;
`;

const EmailInput = styled.input`
    vertical-align: middle;
    width: 404px;
    height: 44px;
    padding-left: 16px;
    border: 1px solid #FF5959;
    font-size: 24px;

    @media screen and (max-width: 1190px) {
		flex-direction: column;
        max-width: calc(100% - 44px);
    }

`;
const EmailButton = styled.button`
    width: 44px;
    height: 44px;
    vertical-align: middle;
    background-color: #FF5959;
    border: 1px solid #FF5959;
    color: white;
    font-size: 36px;
`;

const FooterMiddle = styled.div`
    max-width: 1200px;
    height: 116px;
    margin: 34px auto;
    
`;
const FooterTitleDiv = styled.div`
    color: #201C16;
    font-weight: bold;
    font-size: 14px;
    width: 246px;
    border-bottom: 1px solid #E3E3E3;
`;
const IconSize = styled.span`
    width: 20px;
`;

const Footer = () => {
    return (
        <footer className='footer'>
            <EmailDiv>
            <FlexRow>
                <Subscribe>SUBSCRIBE & GET <span style={{color: '#FFAD5A'}}>NEWS</span></Subscribe>
                <div><EmailInput type="email" placeholder='EMAIL' />< EmailButton>></ EmailButton></div>
            </FlexRow>
            </EmailDiv>
            <FooterMiddle>
                <FlexRow>
                    <FlexRow dir={'column'}>
                        <FooterTitleDiv><IconSize><ContuctUsSvg /></IconSize> CONTACT US</FooterTitleDiv>
                    </FlexRow>
                </FlexRow>
            </FooterMiddle>

           


            <div className='footer_bottom'>
                <button>iji</button>
                <button>iji</button>
                <button>iji</button>
                <button>iji</button>
                <button>iji</button>
                <button>iji</button>
                <button>iji</button>
                <button>iji</button>
            </div>
        </footer>
    );
};

export default Footer;
