import React, { Component } from 'react';
import OneAnswerCreater from './OneAnswerCreater';
import styled from 'styled-components'
import {connect} from 'react-redux';


const RadioButton = styled.input`
		width:2em;
		height:1em;
`;
const Input = styled.input`
	margin: 10px;
	padding: 10px 5px;
	width: 80%;
	height: 1.15em;
	font-size: 16px;
`;

const Ul = styled.ul`
	margin:0;
	padding: 0;
	list-style: none;
	width: 100%;
`;
const Li = styled.li`

`;
const FlexContainer = styled.div`
	display: flex;
	margin: auto;
	padding: 0 10px;
  margin-top: 1em;
  align-items: center;
  justify-content: space-between;
  width: ${props => props.width + '%' || '100%'};
  flex-direction: ${props => props.dir || 'row'};
  box-sizing: border-box;
`;
const Button = styled.button`
	margin: 5px;
	font-size: 18px;
  background: transparent;
  border-radius: 5px;
  border: 1px solid ${props => props.color  || ''};
  background-color: ${props => props.color || 'white'};
  width: ${props => props.width + 'em' || '2em'};
  height:${props => props.height + 'px' || '10px'};
`;
const TitleField = styled.textarea`
  rows: ${props => props.rows  || '4'};
  width: 100%;
`;
class QuestionCreater extends Component {

  componentDidMount() {
    this.answersListCreater();
  }
  addAnswer = () => {
   	let newAnswer = {id: Date.now(), title: '', isRight: false}
    this.props.addAnswer(newAnswer);
  }
  isValid = (e) => {
  	switch (e.target.id) {
  		case 'score':
  			console.log('yes');
  			return	!e.target.value || e.target.value === 0 || e.target.setCustomValidity('Please input score of question');
  		case 'minutes':
  			return !e.target.value || e.target.value === 0 || 'Please input duration of question';
  		case 'title':
  			return !e.target.value || 'Please input title of question';
  		case 'radio':
  			return !e.target.value || 'Please input right answer of question';
  		default:
  			return !e.target.value || 'Please input answer';
  	}
  }
  answersListCreater = () => {
      return ( 
        <Ul>
          {this.props.answers.map(input => (
            <Li key={input.id}>
            	<FlexContainer >
	              <RadioButton
	              	required
	              	id='radio'
	              	onInvalid = {this.isValid}
	              	type='radio'
	              	name='isRight' 
	                value={input.id} 
	                onChange={() => this.props.rightAnswerChosen(input.id)}
	              />
	              <OneAnswerCreater
	                id={input.id} 
	                isRight={input.id === this.props.isRight ? true : false}
	              />
	              <Button 
	              	width={2} 
	              	onClick={() => this.deleteChosenAnswer(input.id)}
	              	type='button'
	              > 
	              	X 
	              </Button>
	            </FlexContainer>
            </Li>))}
        </Ul>
      )
  }   
  inputsCountChangeHandler = (e) => {
      e.target.id === 'plus' && this.props.count <= 4 && this.addAnswer();
   		e.target.id === 'minus' && this.props.count >= 3 && this.props.decreaseAnswersCount();
      this.answersListCreater();
  }
  deleteChosenAnswer = (id) => {
   	for (let i = 0; i < this.props.answers.length; i++) {
    	this.props.isRight === id && this.props.rightAnswerChosen(0);
    	break;
    }
    	this.props.count > 2 && this.props.deleteChosenAnswer(id);
  }
  submitHandler = (e) => {
  	e.preventDefault();
  }
  clearWordFromSpaces = (word) => {
		return  word.replace(/^[ ]+/g, '').replace(/\s*$/, '');
	}
	addQuestion = () => {

	}
    render() {
      return (
        <div>
          <FlexContainer width={100} dir={'column'}>
          	<form onSumbmit={this.submitHandler}>
          	<FlexContainer dir={'column'}>
          		<span>Input Question</span>
          		<TitleField
          			required
          			rows={3} 
          			value={this.props.title}
          			id='title'
          			onChange={(e) => this.props.questionTitleHandler(this.clearWordFromSpaces(e.target.value))}
          		/> 
          	</FlexContainer>
          	<FlexContainer width={100}>
          		<FlexContainer dir='column' >
          			Input score of question
          			<Input 
          				required
          				type='number' 
          				min='0'
          				id='score'
          				placeholder='Input score of question'
          				value={this.props.score}
          				onChange={(e) => this.props.scoreHandler(+e.target.value)}

          		/>
          	 	</FlexContainer>
          		<FlexContainer dir='column' >
          			Input duration in minutes
          			<Input 
          				required
          				type='number' 
          				min='0'
          				id='minutes'  
          				placeholder='Input ending time of question'
          				value={this.props.time}
          				onChange={(e) => this.props.timeHandler(+e.target.value )}
          			/>
          	 	</FlexContainer>
          	</FlexContainer>
            <FlexContainer dir={'column'}>
	            <FlexContainer width={60}>
	              <span>Count of answers</span> 
	                <Button type='button' width={2} height={30} onClick={this. inputsCountChangeHandler} id='minus'> - </Button>
	                  {this.props.count}
	                <Button type='button' width={2} height={30} onClick={this. inputsCountChangeHandler} id='plus'> + </Button>
	            </FlexContainer>
           	{this.answersListCreater()}

            <FlexContainer>
		        <Button width={10} onClick={this.addQuestion}> Add answer </Button>
		        </FlexContainer>
		        </FlexContainer>
		      </form>
        </FlexContainer>
        </div>
      );
    }
}
function mapStateToProps(state) {
  return {
  	answers: state.question.answers,
  	count: state.question.answersInputCount,
  	isRight: state.question.isRight,
  	score: state.question.score,
  	time: state.question.time,
  }
}
function mapDispatchToProps(dispatch) {
  return {
    addAnswer: (answers) => dispatch(addAnswer(answers)),
    decreaseAnswersCount: () => dispatch(decreaseAnswersCount()),
    deleteChosenAnswer: (id) => dispatch(deleteChosenAnswer(id)),
    rightAnswerChosen: (isRight) => dispatch(rightAnswerChosen(isRight)),
    questionTitleHandler: (title) => dispatch(questionTitleHandler(title)),
   	timeHandler: (time) => dispatch(timeHandler(time)),
   	scoreHandler: (score) => dispatch(scoreHandler(score)),
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(QuestionCreater)
