import React, { Component } from 'react';
import OneAnswerCreater from './OneAnswerCreater';
import styled from 'styled-components'
import {connect} from 'react-redux';
import {addAnswer, decreaseAnswersCount, deleteChosenAnswer, 
	rightAnswerChosen, questionTitleHandler} from '../../../store/actions/createAnswer';

const RadioButton = styled.input`
		width:2em;
		height:2em;

	`;
const Date = styled.input`
	width: 10em;
	height: 1.1em;
`;
const Time = styled.input`
	width: 10em;
	height: 1em;
`;
const Ul = styled.ul`
	list-style: none;
	width: 100%;
`;
const Li = styled.li`

`;
const FlexContainer = styled.div`
	display: flex;
	margin: auto;
  margin-top: 1em;
  align-items: center;
  justify-content: space-between;
  width: ${props => props.width + 'em' || '100%'};
  flex-direction: ${props => props.dir || 'row'};
`;
const Button = styled.button`
	margin: 5px;
  font-size: 18px;
  background: transparent;
  border-radius: 3px;
  border: 1px solid ${props => props.color  || ''};
  background-color: ${props => props.color || 'white'};
  width: ${props => props.width + 'em' || '2em'};
  height:${props => props.height + 'px' || '10px'};
`;
const TitleField = styled.textarea`
  rows: ${props => props.rows  || '4'};
  width: 100%;
`;
class QuestionCreater extends Component {
  componentDidMount() {
    this.answersListCreater();
  }
  addAnswer = () => {
   	let newAnswer = {id: Date.now(), title: '', isRight: false}
    this.props.addAnswer(newAnswer);
  }
  answersListCreater = () => {
      return ( 
        <Ul>
          {this.props.answers.map(input => (
            <Li key={input.id}>
            	<FlexContainer width={40}>
	              <RadioButton
	              	type='radio'
	              	name='isRight' 
	                value={input.id} 
	                onChange={() => this.props.rightAnswerChosen(input.id)}
	              />
	              <OneAnswerCreater 
	                id={input.id} 
	                isRight={input.id === this.props.isRight ? true : false}
	              />
	              <Button width={2} onClick={() => this.deleteChosenAnswer(input.id)}> X </Button>
	            </FlexContainer>
            </Li>))}
        </Ul>
      )
  }   
  inputsCountChangeHandler = (e) => {
      e.target.id === 'plus' && this.props.count <= 4 && this.addAnswer();
   		e.target.id === 'minus' && this.props.count >= 3 && this.props.decreaseAnswersCount();
      this.answersListCreater();
  }
  deleteChosenAnswer = (id) => {
   	for (let i = 0; i < this.props.answers.length; i++) {
    	this.props.isRight === id && this.props.rightAnswerChosen(0);
    	break;
    }
    	this.props.count > 2 && this.props.deleteChosenAnswer(id);
    }
    render() {
      return (
        <div>
          <FlexContainer width={50} dir={'column'}>
          	<FlexContainer width={36} dir={'column'}>
          		<span>Input Question</span>
          		<TitleField 
          			rows={3} 
          			value={this.props.title}
          			onChange={(e) => this.props.questionTitleHandler(e.target.value)}
          		/> 
          	</FlexContainer>
          	<FlexContainer>
          		<Date type='date' />
          		<Time type='number' min='0' />
          	</FlexContainer>

            <FlexContainer dir={'column'}>
	            <FlexContainer width={15}>
	              <span>Count of answers</span> 
	                <Button width={2} height={20} onClick={this. inputsCountChangeHandler} id='minus'> - </Button>
	                  {this.props.count}
	                <Button width={2} height={20} onClick={this. inputsCountChangeHandler} id='plus'> + </Button>
	            </FlexContainer>
           	{this.answersListCreater()}
           	</FlexContainer>
        </FlexContainer>
        </div>
      );
    }
}
function mapStateToProps(state) {
  return {
  	answers: state.question.answers,
  	count: state.question.answersInputCount,
  	isRight: state.question.isRight,
  }
}
function mapDispatchToProps(dispatch) {
  return {
    addAnswer: (answers) => dispatch(addAnswer(answers)),
    decreaseAnswersCount: () => dispatch(decreaseAnswersCount()),
    deleteChosenAnswer: (id) => dispatch(deleteChosenAnswer(id)),
    rightAnswerChosen: (isRight) => dispatch(rightAnswerChosen(isRight)),
    questionTitleHandler: (title) => dispatch(questionTitleHandler(title)),
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(QuestionCreater)
