import React from 'react';
import {connect} from 'react-redux';
import {setAnswersValues} from '../../../store/actions/createAnswer';
import styled from 'styled-components'

const AnswersField = styled.textarea`
  rows: ${props => props.rows  || '4'};
  width: 100%;
`;

class OneAnswerCreater extends React.Component {
	inputChangeHandler = (e) => {
   this.title =  this.clearWordFromSpaces(e.target.value);
   this.getInputValue();
  }
	getInputValue = () => {
    let answers = this.props.answers;
    let id = this.props.id;
		let isRight = this.props.isRight;
	  for(let i = 0; i < answers.length; i++) {
	    if (answers[i].id === id) {
        answers[i].title = this.title;
        answers[i].isRight = isRight;  
      } 
    }
    this.props.setAnswersValues(answers);
  }
  clearWordFromSpaces = (word) => {
    return  word.replace(/^[ ]+/g, '').replace(/\s*$/, '');
  }
 	render() {
    return ( 
      <AnswersField required value={this.title} onChange={this.inputChangeHandler}/>
    )
  }
}

function mapStateToProps(state) {
  return {answers: state.question.answers}
}
function mapDispatchToProps(dispatch) {
  return {
    setAnswersValues: (answers) => dispatch(setAnswersValues(answers))
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(OneAnswerCreater)
