import React, { Component } from 'react';
import styled, {css} from 'styled-components'
import js_svg from './icons/js.svg';
//import cplus_svg from './img/Cplus.svg';
import php_svg from './icons/php.svg';
import react_svg from './icons/react.svg';
//import css_svg from './img/C++.svg';
import sharp_svg from './icons/csharp.svg';
//import html_svg from './img/html.svg';


const TestBlock = styled.div`
	width: 350px;
	heigt: 450px;
	border: 1px solid rgba(220, 220, 220, 1);
	box-shadow: 0px 3px 6px rgba(0, 0, 0, 0.16);
	box-sizing: border-box;

`;
const Details = styled.div`
	padding: 16px;
`;
const TestTitle = styled.div`
	font-size: 20px;
	color: #4F9DA6;
	font-weight: bold;
	margin-bottom: 8px;

`;
const Company = styled.div`
	font-size: 16px;
	color: #FFAD5A;
	font-weight: bold;
	margin-bottom: 8px;
`;

const Data = styled.span`
	font-size: 16px;
	color: #4F9DA6;
	margin-bottom: 8px;

`;

const ImgDiv = styled.div`
	width: 350px;
`;

const DataTitle = styled.div`
	font-size: 16px;
	color: black;
	margin-bottom: 8px;
`

const Button = styled.button`
	margin: 0 26px 26px 0;
    width: ${props => props.width || '90px'};  
    height: 44px;
    border: 0;
    border-radius: 4px;
    background-color:rgba(255, 89, 89, 1);
	color: white;
	box-shadow: 0px 3px 6px rgba(0, 0, 0, 0.16);
    font-weight: bold;
    font-size: 20px;
    box-sizing: border-box;
`;
const ButtonDiv = styled.div`
	
	text-align: right;
`;

export default class TestComponent extends Component {
	constructor(props){
		super(props);
		this.state = {
			id: this.props.id,
			img: '',
			title: this.props.title,
			company: this.props.company,
			passes: this.props.passes,
			deadline: this.props.deadline,
        }
	}
	componentDidMount() {
		this.chooseImg(this.props.type)
	}
	chooseImg = (type) => {
		let img = '';
		switch (this.props.type) {
			case 'JavaScript' :
				return img = {js_svg};
			case 'PHP' :
				return img = {php_svg};
			case 'C#' :
				return img = {sharp_svg};
			case 'React' :
				return img = {react_svg};
		
		}

		this.setState({img: img});
	}
    render() {
        return(
			<TestBlock>
				<ImgDiv>
				
					<object type="image/svg+xml" name="myicon" 
						data={js_svg} width="100%" height="100%" >
						Please upate your brouser
					</object>
					
				</ImgDiv>
				<Details>
					<TestTitle>
							{/* {this.state.title} */} js Foundemantals
					</TestTitle>
					<Company>
						{/* {this.state.company} */} Company
					</Company>
					<DataTitle>
						Passes: 
						<Data>
							{/* {this.props.passes} */} 0
						</Data>
					</DataTitle>
					<DataTitle>
						Deadline:
						<Data>
							{/* {this.props.deadline} */}10/12/18
						</Data>
					</DataTitle>
				</Details>
				<ButtonDiv content={'end'} dir={'row'}>
					<Button >Add ></Button>
				</ButtonDiv>
			</TestBlock>
        );
    
	}
}

