import React from 'react';
import OneAnswerCreater from './OneAnswerCreater';
import styled from 'styled-components'
import {connect} from 'react-redux';
import {createAnswers} from '../../../store/actions/createAnswer';


const FlexContainer = styled.div`
    margin-top: 10px;
    display: flex;
    align-items: center;
    justify-content: space-between;
    width: ${props => props.width + 'px' || ''};
`;
const Button = styled.button`
  padding: 2px;
  background: transparent;
  border-radius: 3px;
  border: 1px solid ${props => props.color  || 'grey'};
  background-color: ${props => props.color || 'white'};
  width: ${props => props.width + 'px' || ''};
  height:${props => props.height+ 'px' || ''};
`;

class QuestionCreater extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isRight: 0,
            answersInputCount: 2,
        };
    }
    rightAnswerChangeHandler = (e) => {
        this.setState({isRight: +e.target.value});
    }
    generateArray = () => {
        this.ans = [];
        for (let i = 1; i <= this.state.answersInputCount; i++) {
            this.ans.push({id: i, title: '', isRight: i === this.state.isRight ? true : false})
        }
    }
    answersListCreater = () => {
        this.generateArray();
        return this.props.answers.map((input, index)=> {
            return (
                <FlexContainer key={index + 1} width={600}>
                    <input type='radio' 
                        value={index + 1} 
                        name='rightAnswer' 
                        onChange={this.rightAnswerChangeHandler}
                    />
                    <OneAnswerCreater 
                        id={index + 1} 
                        isRight={index + 1 === this.state.isRight ? true : false}
                        getInputValue={this.getInputValue}
                        answers={this.ans}
                    />
                </FlexContainer>
            )
        });
    }
    componentDidMount() {
        this.generateArray();
        this.answersListCreater();
    }
    inputsCountChangeHandler = (e) => {
        return new Promise((resolve, reject) => {
            this.setState({answersInputCount: +e.target.value});
                resolve(this.state.answersInputCount)
            })
            .then (number => {
                this.generateArray();
                this.answersListCreater();
                this.props.createAnswers(this.ans);
            })
    }
    render() {
        return (
            <div>
                <FlexContainer width={500}>
                    <FlexContainer width={200}>
                        <span>Count of answers</span> 
                        {' '}<Button width={20} height={20}>+</Button>
                        {' '}{this.state.answersInputCount}
                        {' '}<Button width={20} height={20}>-</Button>
                    </FlexContainer>
                    {this.answersListCreater()}
                </FlexContainer>

            </div>
        );


    }
}
function mapStateToProps(state) {
  return {answers: state.quiz.answers}
}
function mapDispatchToProps(dispatch) {
  return {
    createAnswers: (answers) => dispatch(createAnswers(answers)),
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(QuestionCreater)
