import React, { Component } from 'react';
import styled from 'styled-components'
import js_svg from '../../images/typeIcons/js.svg';
import cplus_svg from '../../images/typeIcons/cplus.svg';
import php_svg from '../../images/typeIcons/php.svg';
import react_svg from '../../images/typeIcons/react.svg';
import css_svg from '../../images/typeIcons/css.svg';
import sharp_svg from '../../images/typeIcons/csharp.svg';
import non_svg from '../../images/typeIcons/non-type.svg';
import html_svg from '../../images/typeIcons/html.svg';
import { connect } from 'react-redux';
import { addUserTest } from '../../store/actions/appAction';
import { firebase } from '../../firebase/firebase';

const TestBlock = styled.div`
	position: relative;
	width: 100%;
	border: 1px solid rgba(220, 220, 220, 1);
	border-radius: 4px;
	box-shadow: 0px 3px 6px rgba(0, 0, 0, 0.16);
	box-sizing: border-box;
	:hover {
		cursor: pointer;
	}
`;

const Details = styled.div`
	padding: 16px;
`;

const TestTitle = styled.div`
	font-size: 20px;
	color: #4F9DA6;
	font-weight: bold;
	margin-bottom: 8px;
`;

const Company = styled.div`
	font-size: 16px;
	color: #FFAD5A;
	font-weight: bold;
	margin-bottom: 8px;
`;

const Data = styled.span`
	font-size: 16px;
	color: #4F9DA6;
	margin-bottom: 8px;
`;

const ImgDiv = styled.div`
	width: 100%;
	box-sizing: border-box;
	background-image:
`;

const DataTitle = styled.div`
	font-size: 16px;
	color: black;
	margin-bottom: 8px;
`;

const Button = styled.button`
	margin: 0 26px 26px 0;
    width: ${props => props.width || '90px'};  
    height: 44px;
    border: 0;
    border-radius: 4px;
    background-color: ${props => props.disabled ? '#4F9DA6' :'rgba(255, 89, 89, 1)'};
	color: white;
	box-shadow: 0px 3px 6px rgba(0, 0, 0, 0.16);
    font-weight: bold;
    font-size: 20px;
	box-sizing: border-box;
	: hover {
		cursor: ${props => props.disabled ? ''  : 'pointer' }
	}
`;

const ButtonDiv = styled.div`
	text-align: right;
`;
const Img = styled.img`
	width: 100%;
	height: 100%;
`;


class TestComponent extends Component {
	state = {
		testExists: false,
	}

	convertDate = (date) => {
		let today = new Date(date);
		let dd = today.getDate();
		let mm = today.getMonth() + 1;
		let yyyy = today.getFullYear();

		if (dd < 10) {
			dd = '0' + dd
		}

		if (mm < 10) {
			mm = '0' + mm
		}
		return today = dd + '/' + mm + '/' + yyyy;
	}

	chooseImg = () => {
		switch (this.props.test.testType) {
			case 'JavaScript':
				return js_svg;
			case 'PHP':
				return php_svg;
			case 'C#':
				return sharp_svg;
			case 'React':
				return react_svg;
			case 'HTML':
				return html_svg;
			case 'CSS':
				return css_svg;
			case 'C++':
				return cplus_svg;
			default:
				return non_svg;
		}
	}

	
	add = (test) => {
		if (this.props.userType === 'user') {
			let userUrl = this.props.user.id;
			firebase.database().ref(`user/${this.props.user.id}/tests`).once('value',  (snapshot)=> {
				if (snapshot.hasChild(`${test.id}`)) {
				
				} else {
					this.props.userTestAdded();
					this.props.addUserTest(test);
					let userRef = firebase.database().ref(`user/${userUrl}`);
					userRef.child('tests').child(`${test.id}`).set({...test, userScore: -1});
					firebase.database().ref(`tests/${test.id}`).child(`added`).set(true);
				}
			});
		} else {
			this.props.testAddClicked();
		}
	}
	render() {
		let test = this.props.test;
		return (
			<>
				<TestBlock>
					<ImgDiv>
						<Img src={this.chooseImg()} />
					</ImgDiv>
					<Details>
						<TestTitle>
							{test.testTitle}
						</TestTitle>
						<Company>
							{test.company}
						</Company>
						<DataTitle>
							Passes: {' '}
							<Data>
								{!test.passers ? 0 : test.passers.length}
							</Data>
						</DataTitle>
						<DataTitle>
							Deadline:{' '}
							<Data>
								{this.convertDate(test.testDeadline)}
							</Data>
						</DataTitle>
					</Details>

					<ButtonDiv>
						{
							this.props.userType !== 'company'
								? <Button 
									onClick={() => this.add(test)}
									disabled = {test.added ? true : false}>
									{test.added ? 'Added' : 'Add'}
 								</Button>
								: ""
						}

					</ButtonDiv>
				</TestBlock>
			</>
		);
	}
}

function mapStateToProps(state) {
	return {
		userLogin: state.appReducer.userLogin,
		userType: state.appReducer.userType,

	}
}

function mapDispatchToProps(dispatch) {
	return {
		addUserTest: test => dispatch(addUserTest(test)),
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(TestComponent)
