import {ADD_ANSWER, SET_ANSWERS_VALUES, DECREASE_ANSWERS_COUNT} from './actionTypes'

export function addAnswer(answer) {
  console.log('this is action');
  return {
    type: ADD_ANSWER,
    answer
  }
}
export function setAnswersValues(answers) {
  console.log('this is action');
  return {
    type: SET_ANSWERS_VALUES,
    answers
  }
}
export function decreaseAnwersCount() {
  console.log('this is action');
  return {
    type: DECREASE_ANSWERS_COUNT,
  }
}
