import React, { Component } from 'react';
import { connect } from 'react-redux';
import TestRender from '../TestRenderComponents/TestRender';
import styled, {css} from 'styled-components'

let percent;
const styles = { transition: 'all 1s linear' }

function resp(){
	if (window.innerWidth <= 500) {
		percent = 90
	} else {
		percent = 70
	}
}

resp()

window.onresize=function(){ resp() }


// styled carousel

const CarouselContainer = styled.div`
  width: 960px;
  max-width: 100%;
  margin: 10px auto;
  padding-bottom: 60%; 
  box-sizing: border-box;
  position: relative;
  overflow: hidden;
`

const ContainerItem = styled.div`
  position: relative;
  display: flex;
  width: 3200px;
  max-width: 333.3%;
  padding: 5px;
  box-sizing: border-box;
  @media screen and (max-width: 500px){
    max-width: unset;
    width: 1000%;
  }
`
const ArrowContent = styled.div`text-align: center;`

const Arrow = styled.span`
  display: inline-block;
  margin: 15px;
  padding: 8px 12px;
  background: red;
  color:#ffffff;
  cursor: pointer;
  border-radius: 4px;
`

class TestSlider extends Component {
	constructor(props){
		super(props);

		this.state = {
			translate : 0,
			tests: this.props.tests,
		}
    }
    setImage = () => {

	}

	componentDidUpdate(prevProps, prevState) {
        if (this.props.tests === true && this.props.tests !== prevProps.tests.testsLoaded) {
            this.setState({tests: this.props.tests});
        }
	}
	
	componentDidMount () {

		setInterval(() => {
			this.setState({ translate : this.state.translate === -percent ? 0 : this.state.translate - 10 })
		}, 10000)
	}

	ToLeft () {
		this.setState({ translate : this.state.translate === 0 ? -percent : this.state.translate + 10 })
	}	

	ToRight () {
		this.setState({ translate : this.state.translate === -percent ? 0 : this.state.translate - 10 })
	}

	render() {
	let tests = [];
		if (this.state.tests) {
		   tests = Object.keys(this.state.tests[0]);
		   	console.log(this.state.tests);
	    } 
	
		
		return (
			<div>
				<ArrowContent className="arrows">
					<Arrow onClick={this.ToLeft.bind(this)}>Left</Arrow>
					<Arrow onClick={this.ToRight.bind(this)}>Right</Arrow>
				</ArrowContent>
				<CarouselContainer>
					<ContainerItem style={{ ...styles, transform: `translateX(${this.state.translate}%)` }}>
					{
						this.state.tests ?
						tests.map( (item, index) => {
							return (
								 <TestRender test={item} key={item.id}/>
								
							);
						})
						: ''
					}
					</ContainerItem>
				</CarouselContainer>
			</div>
		);
	}
}

function mapStateToProps(state) {
	return {
		tests: state.appReducer.tests,
	}
	
}

export default connect(mapStateToProps, null)(TestSlider)
