import React from 'react';
import OneAnswerCreater from './OneAnswerCreater';
import styled from 'styled-components'
import {connect} from 'react-redux';
import {createAnswers} from '../../../store/actions/createAnswer';

const AnswerDiv = styled.div`
    display: flex;
    align-items: center;
`;
const Button = styled.button`
  background: transparent;
  border-radius: 3px;
  border: 2px solid ${props => props.color  || 'grey'};
  margin: 0 1em;
  padding: 0.25em 1em;
  color: ${props => props.color || 'white'};
  width: ${props => props.width + 'px' || ''};
  height:${props => props.height+ 'px' || ''};
`;

class QuestionCreater extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isRight: 0,
            answersInputCount: 2,
        };
    }
    rightAnswerChangeHandler = (e) => {
        this.setState({isRight: +e.target.value});
    }
    generateArray = () => {
        this.ans = [];
        for (let i = 1; i <= this.state.answersInputCount; i++) {
            this.ans.push({id: i, title: '', isRight: i === this.state.isRight ? true : false})
        }
    }
    answersListCreater = () => {
        this.generateArray();
        return this.props.answers.map((input, index)=> {
            return (
                <AnswerDiv key={index + 1}>
                    <input type='radio' 
                        value={index + 1} 
                        name='rightAnswer' 
                        onChange={this.rightAnswerChangeHandler}
                    />
                    <OneAnswerCreater 
                        id={index + 1} 
                        isRight={index + 1 === this.state.isRight ? true : false}
                        getInputValue={this.getInputValue}
                        answers={this.ans}
                    />
                </AnswerDiv>
            )
        });
    }
    componentDidMount() {
        this.generateArray();
        this.answersListCreater();
    }
    inputsCountChangeHandler = (e) => {
        return new Promise((resolve, reject) => {
            this.setState({answersInputCount: +e.target.value});
                resolve(this.state.answersInputCount)
            })
            .then (number => {
                this.generateArray();
                this.answersListCreater();
                this.props.createAnswers(this.ans);
            })
    }
    render() {
        return (
            <div>
                <span>Count of answers <Button width={20} height={20}>+</Button>{this.state.answersInputCount}<Button width={20} height={20}>-</Button></span>
                {this.state.radioButtons.map(rb => (
                    <label key={rb.count}>{rb.count}
                        <input
                            value={rb.count}
                            type="radio"
                            name='count'
                            defaultChecked={rb.checked}
                            onChange={this.inputsCountChangeHandler}
                        />
                    </label>
                ))}
                {this.answersListCreater()}
            </div>
        );


    }
}
function mapStateToProps(state) {
  return {answers: state.quiz.answers}
}
function mapDispatchToProps(dispatch) {
  return {
    createAnswers: (answers) => dispatch(createAnswers(answers)),
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(QuestionCreater)
