import React, { Component } from 'react';
import styled, {css} from 'styled-components'
import { connect } from 'react-redux';
import img from './img/rawpixel-558596-unsplash.jpg'

const Main = styled.div`
	margin: auto;
	max-width: 1920px;
	font-size: 24px;
    overflow: auto;
    font-family: 'Roboto', sans-serif;
`; 
const Header = styled.div`
    position: relative;
    width: 100%;   
    height: 700px;		
    background-image: url(${img});
    background-position: center; 
    background-repeat: no-repeat; 
    background-size: cover;
    box-sizing: border-box;
`;

const GetStartedDiv = styled.div`
    position: absolute;
    bottom:0;
    color: white;
    width: 100%;   
    height: 100px;	
    z-index: 1;
    background-color: rgba(20, 18, 24, 0.75);
    font-size: 24px;	
`;

const FlexRow = styled.div`
    display: flex;
    margin: auto;
    width: 1200px;
    background-color: yellow;
    flex-direction: row;
	flex-wrap: wrap;
    justify-content: space-between;
    align-items: center:
    
	box-sizing: border-box;

	@media screen and (max-width: 1190px) {
		flex-direction: column;
		min-width: 100%;
	}
`;
const GetsStartedText = styled.div`
    display: block;
    width: 1018px;
    word-wrap: break-word;
    box-sizing: border-box;   
`;
const GetStartedButton = styled.button`
    width: 155px;
    height: 60px;
    border: 0;
    border-radius: 4px;
    padding: 16px;
    box-sizing: border-box;
    background-color:rgba(255, 89, 89, 1);
    color: white;
    font-size: 18px;	

`;

class HomePage extends Component {
	constructor(props) {
		super(props);
		this.state ={};
	}

	render() {
		return (
			<Main>
                <Header>
                    <GetStartedDiv>
                        <FlexRow>
                            <GetsStartedText>
                                DigiLearn is an all-in-one platform 
                                that makes it easy to create online tests, 
                                launch programming companies, build landing pages, 
                                and design the perfect website.
                            </GetsStartedText>
                            <div>
                                <GetStartedButton>
                                    GET STARTED
                                </GetStartedButton>
                            </div>
                        </FlexRow>
                    </GetStartedDiv>
                </Header>
            </Main>
		);
	}
}

function mapStateToProps(state) {
	return {
	
	}
}

export default connect(mapStateToProps, null)(HomePage)
