import React, { Component } from 'react';
import styled, {css} from 'styled-components'
import { connect } from 'react-redux';
import axios from 'axios';
import Timer from './Timer';
import TestFinished from './TestFinished';


const Main = styled.div`
	margin: auto;
	max-width: 1200px;
	font-size: 24px;
	color: white;
	overflow: none;
`;
const FlexRow = styled.div`
	display: flex;
	flex-direction: ${props => props.dir || 'row'};
	flex-wrap: wrap;
	margin: 16px 0;
	justify-content: space-between;
	align-items: center;
	width: ${props => props.width || '100%'};
	height: ${props => props.height || '100%'};
	box-sizing: border-box;

	@media screen and (max-width: 1190px) {
		flex-direction: column;
		min-width: 100%;
	}
`;
const FlexChild = styled.div`
	width:  ${props => props.width || '25%'};
	border-radius: 4px;
	box-sizing: border-box;
		   
	@media screen and (max-width: 1190px) {
		margin: 5px;
		min-width: 100%;
	}
`;
const FlexChildAnswer = styled.div`
	padding: 16px 32px;
	word-wrap: break-word;
	text-align: center;
	background-color: #4F9DA6;
	border-radius: 4px;
	width:  ${props => props.width || '25%'};
	min-height: 60px;
	box-sizing: border-box;
		   
	@media screen and (max-width: 1190px) {
		margin: 5px;
		min-width: 100%;
	}
	:hover {
		background-color: #3A7C84;
	}
`;
const QuestionNumber = styled.div`
	padding-left: 144px;
	font-size: 24px;
	color: rgba(16, 5, 41, 1);
	height: 44px;
	width: 100%;
	text-align: center;
	line-height: 44px;
	box-sizing: border-box;
`;
const AnswerTitle = styled.div`
	margin-bottom: 16px;
	padding: 32px 16px;
	background-color: rgba(20, 18, 24, 0.8);
	width: 100%;
	min-height: 100px;
	box-sizing: border-box;
	
`;

class TestPassPanel extends Component {
	constructor(props) {
		super(props);
		this.state = {
			testData: {},
			testEnd: false,
			totalScore: 0,
		}
    }
   componentDidMount() {	

	axios.get('https://test-project-4ab6b.firebaseio.com/tests/-LS0j3a0GongklJjoPEw.json')
		.then((response) => {
			console.log(response.data.questions)
			this.setState({testData: response.data});
			return this.state.testData
		}).then((testData) => {
			this.setState({testLoaded: true, currentQuesion: testData.questions[0]})
		})	
	}
	currentQuesion = () => {


	}

	testEnded = () => {
		this.setState({testEnd: true});
	}
	render() {
		let questions = [];
		if (this.state.testLoaded) {
			questions = this.state.testData.questions;
		} 
		return (
			<Main>
				{this.state.testLoaded 
		
				}
			</Main>
			
		);
	}
}

function mapStateToProps(state) {
	return {
	}
}
function mapDispatchToProps(dispatch) {
	return {
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(TestPassPanel)
