		// let questions = this.state.questions.map(question => {
		// 	return {...question}
		// })
		//console.log(this.state.questions.id)
		
		console.log(this.state.testData.testTitle);
		return (
			<Main>
				 {
				// this.state.testData[8].questions.map(question => (
				// 	<FlexRow>
				//  		<QuestionNumber>Question 1/10</QuestionNumber>
				//  		<FlexChild>{question.answers[0].title}</FlexChild>
				//  	</FlexRow>
				//  	))
				} 
			</Main>
		);
	}
}

function mapStateToProps(state) {
	return {
		totalScore: state.test.totalScore,
		submitted: state.test.submitted,
	}
}
function mapDispatchToProps(dispatch) {
	return {
	 submittedTrue: () => dispatch( submittedTrue()),
	 submittedFalse: () => dispatch( submittedFalse()),
	 increaseTotalScore: (score) => dispatch(increaseTotalScore(score)),
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(TestPassPanel)
